class Solution {
    private static final char PLAYER = 'S';
    private static final char EMPTY = '.';
    private static final char WALL = '#';
    private static final char BOX = 'B';
    private static final char TARGET = 'T';
    private int rowLen;
    private int colLen;
    private boolean[][][][][] canReach;
    private int[][] DIRS = {{1, 0}, {-1, 0}, {0, 1}, {0, -1}};


    public int minPushBox(char[][] grid) {
        rowLen = grid.length;
        colLen = grid[0].length;
        int[] usefulCoordinates = getCoordinates(grid);    
        populateCanReach(grid);
        int bRow = usefulCoordinates[0];
        int bCol = usefulCoordinates[1];
        int pRow = usefulCoordinates[2];
        int pCol = usefulCoordinates[3];
        int tRow = usefulCoordinates[4];
        int tCol = usefulCoordinates[5];       
        if (bRow == tRow && bCol == tCol) return 0;
        Queue<int[]> que = new ArrayDeque<>();
        boolean[][][][] visited = new boolean[rowLen][colLen][rowLen][colLen];
        visited[bRow][bCol][pRow][pCol] = true;
        que.offer(new int[] {bRow, bCol, pRow, pCol});
        int step = 0;

        while (!que.isEmpty()) {
            int size = que.size();
            for (int k = 0; k < size; k++) {
                int[] cur = que.poll();
                int curBRow = cur[0];
                int curBCol = cur[1];                
                int curPRow = cur[2];
                int curPCol = cur[3];

                if (curBRow == tRow && curBCol == tCol) return step; 

                for (int i = 0; i < DIRS.length; i++) {
                    int[] dir = DIRS[i];
                    int nextBRow = curBRow + dir[0];
                    int nextBCol = curBCol + dir[1];
                    int pushedDir = i % 2 == 0 ? i + 1 : i - 1;
                    int pushedRow = curBRow + DIRS[pushedDir][0];
                    int pushedCol = curBCol + DIRS[pushedDir][1];
                    if (!isValid(nextBRow, nextBCol) || grid[nextBRow][nextBCol] == WALL) continue;
                    if (!isValid(pushedRow, pushedCol) || grid[pushedRow][pushedCol] == WALL) continue;
                    if (!canReach[curBRow][curBCol][pushedDir][curPRow][curPCol]) continue; 
                    if (visited[nextBRow][nextBCol][curBRow][curBCol]) continue;
                    visited[nextBRow][nextBCol][curBRow][curBCol] = true;
                    que.offer(new int[] {nextBRow, nextBCol, curBRow, curBCol});
                } 
            }
            step++;
        }
        return -1;
    }

    private void populateCanReach(char[][] grid) {
        canReach = new boolean[rowLen][colLen][4][rowLen][colLen];
        for (int row = 0; row < rowLen; row++) {
            for (int col = 0; col < colLen; col++) {
                if (grid[row][col] != EMPTY) continue;
                for (int i = 0; i < DIRS.length; i++) {
                    int nextRow = row + DIRS[i][0];
                    int nextCol = col + DIRS[i][1];
                    if (!isValid(nextRow, nextCol) || grid[nextRow][nextCol] != EMPTY) continue;
                    canReach[row][col][i][row][col] = true; 
                    BFS(nextRow, nextCol, canReach[row][col][i], grid);
                    canReach[row][col][i][row][col] = false;
                }
            }
        }
    }

    private void BFS(int row, int col, boolean[][] visited, char[][] grid) {
        Queue<int[]> que = new ArrayDeque<>();
        que.offer(new int[] {row, col});
        visited[row][col] = true;
        while (!que.isEmpty()) {
            int[] curPoint = que.poll();
            for (int[] dir : DIRS) {
                int nextRow = curPoint[0] + dir[0];
                int nextCol = curPoint[1] + dir[1];
                if (!isValid(nextRow, nextCol) || visited[nextRow][nextCol] || grid[nextRow][nextCol] == WALL) continue;
                visited[nextRow][nextCol] = true;
                que.offer(new int[] {nextRow, nextCol});
            }
        }
    }

    private int[] getCoordinates(char[][] grid) {
        int bRow = -1, bCol = -1;
        int pRow = -1, pCol = -1;
        int tRow = -1, tCol = -1;
        for (int row = 0; row < rowLen; row++) {
            for (int col = 0; col < colLen; col++) {
                if (grid[row][col] == PLAYER) {
                    pRow = row;
                    pCol = col;
                    grid[row][col] = EMPTY;
                }
                if (grid[row][col] == BOX) {
                    bRow = row;
                    bCol = col;
                    grid[row][col] = EMPTY;
                }
                if (grid[row][col] == TARGET) {
                    tRow = row;
                    tCol = col;
                    grid[row][col] = EMPTY;
                } 
            }
        }   
        return new int[] {bRow, bCol, pRow, pCol, tRow, tCol};
    }

    private boolean isValid(int row, int col) {
        return row >= 0 && row < rowLen && col >= 0 && col < colLen;
    }

}
