class Solution {
public:
    string stringigy(int x,int t,int a,int b){
        return to_string(x)+"_"+to_string(t)+"_"+
            to_string(a)+"_"+to_string(b);
    }
    bool check(vector<vector<char>>& nums,int ni,int nj,int i,int j,int x,int y){
        vector<vector<bool>> vis(nums.size(),vector<bool>(nums[0].size(),0));
        vis[i][j] = 1;
        queue<pair<int,int>> q; 
        q.push({i,j});
        while(!q.empty()){
            int s = q.size();
            while(s--){
            auto [i,j] = q.front(); q.pop();
            if(i==ni && j==nj) continue;
            if(i==x && j==y) return 1;
            if(i+1<nums.size() && !vis[i+1][j] && nums[i+1][j]!='#' ){
                q.push({i+1,j}); vis[i+1][j] = 1;
            }
            if(i-1>=0 && !vis[i-1][j] && nums[i-1][j]!='#' ){
                q.push({i-1,j}); vis[i-1][j] = 1;
            }
            if(j+1<nums[0].size() && !vis[i][j+1] && nums[i][j+1]!='#' ){
                q.push({i,j+1}); vis[i][j+1] = 1;
            }
            if(j-1>=0 && !vis[i][j-1] && nums[i][j-1]!='#' ){
                q.push({i,j-1}); vis[i][j-1] = 1;
            }}
        }
        return 0;
    }
    int minPushBox(vector<vector<char>>& nums) {
        int bi,bj,si,sj,ti,tj;
        for(int i = 0; i<nums.size();++i){
            for(int j = 0;j<nums[0].size();++j){
                if(nums[i][j]=='B'){ bi = i,bj = j; }
                if(nums[i][j]=='S'){ si = i,sj = j; }
                if(nums[i][j]=='T'){ ti = i,tj = j; }
            }
        }
        unordered_map<string,int> mp;
        queue<vector<int>> q;
        q.push({bi,bj,si,sj});
