class Solution {
public:
    int dx[4] = {1,0,-1,0};
    int dy[4] = {0,1,0,-1};

    bool isReachable(vector<vector<char>> &grid, int x, int y, int tx, int ty) {
        int n = grid.size();
        int m = grid[0].size();
        bool visited[n][m];
        memset(visited, false, sizeof(visited));
        queue<vector<int>> Q;
        Q.push({x, y});
        visited[x][y] = true;
        while(!Q.empty()) {
            vector<int> F = Q.front();
            if(F[0] == tx && F[1] == ty) {
                return true;
            }
            Q.pop();
            for(int i = 0; i < 4; i++) {
                int nx = F[0] + dx[i];
                int ny = F[1] + dy[i];
                if(nx < 0 || ny < 0 || nx >= n || ny >= m) {
                    continue;
                }
                if(visited[nx][ny] || grid[nx][ny] == '#') {
                    continue;
                }
                visited[nx][ny] = true;
                Q.push({nx, ny});
            }
        }
        return false;
    }

    int minPushBox(vector<vector<char>>& grid) {
        int n = grid.size();
        int m = grid[0].size();
        int sx, sy, bx, by, tx, ty;
        for(int i = 0; i < n; i++) {
            for(int j = 0; j < m; j++) {
                if(grid[i][j] == 'S') {
                    sx = i, sy = j;
                }
                if(grid[i][j] == 'B') {
                    bx = i, by = j;
                }
                if(grid[i][j] == 'T') {
                    tx = i, ty = j;
                }
            }
        }
        bool visited[n][m][n][m];
        memset(visited, false, sizeof(visited));
        queue<vector<int>> Q;
        Q.push({bx, by, sx, sy});
        visited[bx][by][sx][sy] = true;
        int level = 0;
        while(!Q.empty()) {
            int sz = Q.size();
            while(sz--) {
                vector<int> F = Q.front();
                Q.pop();
                bx = F[0], by = F[1], sx = F[2], sy = F[3];
                if(bx == tx && by == ty) {
                    return level;
                }
                grid[bx][by] = '#';    
                for(int i = 0; i < 4; i++) {
                    int nbx = bx + dx[i];
                    int nby = by + dy[i];
                    int nsx = bx - dx[i];
                    int nsy = by - dy[i];

                    if(nbx < 0 || nbx >= n || nby < 0 || nby >= m || nsx < 0 || nsx >= n || nsy < 0 || nsy >= m) {
                        continue;
                    }
                    if(visited[nbx][nby][nsx][nsy] || grid[nbx][nby] == '#' || grid[nsx][nsy] == '#') {
                        continue;
                    }
                    if(isReachable(grid, sx, sy, nsx, nsy)) {
                        visited[nbx][nby][nsx][nsy] = true;
                        Q.push({nbx, nby, nsx, nsy});
                    }
                }
                grid[bx][by] = '.';
            }
            level++;
        }
        return -1;
    }
};
