```go
func minPushBox(grid [][]byte) int {
  var queue [][4]int
  var pushes int
  var playerX, playerY int
  var targetX, targetY int
  var boxX, boxY int
  m, n := len(grid), len(grid[0])
  visited := make(map[[4]int]bool)
  for i := 0; i < len(grid); i++ {
    for j := 0; j < len(grid[i]); j++ {
      if grid[i][j] == 'S' {
        playerX, playerY = i, j
      } else if grid[i][j] == 'B' {
        boxX, boxY = i, j
      } else if grid[i][j] == 'T' {
        targetX, targetY = i, j
      }
    }
  }
  // We've noted down these positions already. Let's clear them so we can easily check
  // reachability for the player to move to the corresponding positions.
  grid[playerX][playerY] = '.'
  grid[boxX][boxY] = '.'
  grid[targetX][targetY] = '.'
  queue = append(queue, [4]int{playerX, playerY, boxX, boxY})
  directions := [4][2]int{{-1, 0}, {0, -1}, {1, 0}, {0, 1}} // top, left, bottom, right
  personDir := [4][2]int{{1, 0}, {0, 1}, {-1, 0}, {0, -1}}
  // We just don't want to be in the same state again where the player and box are in the same position
  visited[[4]int{playerX,playerY,boxX,boxY}] = true
  for len(queue) > 0 {
    var newQueue [][4]int
    for _, state := range queue {
      playerX, playerY, boxX, boxY = state[0], state[1], state[2], state[3]
      if boxX == targetX && boxY == targetY {
        // If the box is currently in the target position, return the number
        // pushes we've done so far.
        return pushes
      }
      grid[boxX][boxY] = 'B'
      for i := 0; i < len(directions); i++ {
        x1, y1 := boxX + directions[i][0], boxY + directions[i][1]
        if x1 >= 0 && x1 < m && y1 >= 0 && y1 < n && grid[x1][y1] == '.' {
          x2, y2 := boxX + personDir[i][0], boxY + personDir[i][1]
          if x2 >= 0 && x2 < m && y2 >= 0 && y2 < n && grid[x2][y2] == '.' && canReach(grid, playerX, playerY, x2, y2) {
            // the player is now in the previous position of the box
            if !visited[[4]int{boxX, boxY, x1, y1}] {
              visited[[4]int{boxX, boxY, x1, y1}] = true
              newQueue = append(newQueue, [4]int{boxX, boxY, x1, y1})
            }
          }
        }
      }
