#include <array>
#include <iostream>
#include <queue>
#include <unordered_set>
#include <vector>

using namespace std;

namespace std {
template <>
struct hash<array<int, 2>> {
  uint16_t operator()(const array<int, 2> &a) const { return a[0] << 8 | a[1]; }
};

template <>
struct hash<array<int, 4>> {
  uint32_t operator()(const array<int, 4> &a) const {
    return a[0] << 24 | a[1] << 16 | a[2] << 8 | a[3];
  }
};
}  // namespace std

/* Use a graph search (BFS) to find if our store-man can reach the cell just
 * behind the box to push. This BFS routine is called inside another BFS to find
 * the shortest moves required for the box to reach the target cell. There is
 * just one nuance. The outer BFS, the one for the box, need to be adjusted to
 * allow the box to visit a previously-visited cell but coming from a different
 * direction. So the BFS state is a 5-tuple {curr_x, curr_y, prev_x, prev_y,
 * moves}. The walls might force our store-man to push the box to a previously
 * visited cell in order to go to a different side of the box. Other than that,
 * this is mostly an implementation problem. We override the
 * std::hash<std::array<int, N>> function object to use unordered_set<array<int,
 * N>> to track visited cells by the store-man and the box.  Since we can visit
 * each cell at most 4 times, and there are m*n cells, the time complexity is
 * O(4mn * O(mn)) = O(4(mn)^2) time = 2.5e7 < 1e8 for m = n = 20. This solution
 * won't give us a TLE.
 */
class Solution {
 public:
  int minPushBox(vector<vector<char>> &grid) {
    auto m = grid.size(), n = grid[0].size();
    int bx, by, sx, sy;
    for (auto i = 0; i < m; ++i)
      for (auto j = 0; j < n; ++j)
        if (grid[i][j] == 'B')
          bx = i, by = j;
        else if (grid[i][j] == 'S')
          sx = i, sy = j;

    const int dirs[4][2] = {{0, 1}, {1, 0}, {0, -1}, {-1, 0}};
    unordered_set<array<int, 2>> man_visited;
    auto updateManVisited = [&](const array<int, 2> sxy) {
      queue<array<int, 2>> sq;
      man_visited.clear();
      sq.push(sxy);
      man_visited.insert(sxy);
      while (!sq.empty()) {
        auto [sx, sy] = sq.front();
        sq.pop();
        for (auto [dx, dy] : dirs) {
          auto nsx = sx + dx;
          auto nsy = sy + dy;
          if (0 <= nsx && nsx < m && 0 <= nsy && nsy < n &&
              (grid[nsx][nsy] == '.' || grid[nsx][nsy] == 'T') &&
              man_visited.find({nsx, nsy}) == man_visited.end()) {
            sq.push({nsx, nsy});
            man_visited.insert({nsx, nsy});
          }
        }
      }
    };

    unordered_set<array<int, 4>> box_visited; /* x, y, sx, sy */
    queue<array<int, 5>> q;                   /* x, y, sx, sy, moves */
    q.push({bx, by, sx, sy, 0});
    grid[sx][sy] = '.';
    while (!q.empty()) {
      auto [bx, by, sx, sy, moves] = q.front();
      q.pop();
      if (grid[bx][by] == 'T') return moves;
      grid[bx][by] = 'B';
      updateManVisited({sx, sy});
      grid[bx][by] = '.';
      for (auto [dx, dy] : dirs) {
        auto nbx = bx + dx;
        auto nby = by + dy;
        auto nsx = bx - dx;
        auto nsy = by - dy;
        if (0 <= nbx && nbx < m && 0 <= nby && nby < n && 0 <= nsx && nsx < m &&
            0 <= nsy && nsy < n &&
            (grid[nbx][nby] == '.' || grid[nbx][nby] == 'T') &&
            man_visited.find({nsx, nsy}) != man_visited.end() &&
            box_visited.find({nbx, nby, nsx, nsy}) == box_visited.end()) {
          q.push({nbx, nby, bx, by, moves + 1});
          box_visited.insert({nbx, nby, nsx, nsy});
        }
      }
    }

    return -1;
  }
};

