class Solution {
public:
    int minPushBox(vector<vector<char>>& grid) {
        int m = grid.size(), n = grid[0].size();
        pi box, man, t;

        // graph traversal array
        int g[] = {1, 0, -1, 0, 1};

        // lambda function to check if vertex is valid
        auto check = [&](int xx, int yy){
            if(0 <= xx && xx < m && 0 <= yy && yy < n && grid[xx][yy] != '#')
                return true;
            return false;
        };

        // searching for box, player and target
        for(int i = 0; i < m; ++i){
            for(int j = 0; j < n; ++j){
                if(grid[i][j] == 'B')
                    box = mp(i, j);
                if(grid[i][j] == 'S')
                    man = mp(i, j);
                if(grid[i][j] == 'T')
                    t = mp(i, j);
            }
        }

		// to check already visited states
        set<tuple<int, int, int, int>> vis;

        // 0-1 BFS
        deque<tuple<int, int, int, int, int> > q;
        q.push_back({man.f, man.s, box.f, box.s, 0});

        while(!q.empty() ){
            int x, y, a, b, d;
            tie(x, y, a, b, d) = q.front();
            q.pop_front();

            // goal node reached
            if(mp(a, b) == t)
               return d;

            auto cur = make_tuple(x, y, a, b);
            // check if already visited
            if(vis.count(cur) )
                continue;

            // if not, mark visited
            vis.insert(cur);

            // visit all neighbors
            for(int i = 0; i < 4; ++i){
                int nx = x + g[i], ny = y + g[i + 1];
                pi nxt = mp(nx, ny), A = mp(a, b);

                if(!check(nx, ny) )
                    continue;

                // if neighbor -> box
                if(nxt == A){
                    // push it in same direction
                    int na = a + g[i], nb = b + g[i + 1];
                    if(!check(na, nb) )
                        continue;

                    // pushing box -> 1 cost operation
                    // so add this state to the last of queue
                    q.push_back({a, b, na, nb, d + 1});
                } else
                    // moving man -> 0 cost operation
                    q.push_front({nx, ny, a, b, d});
            }
        }

        // if target not found
        return -1;
    }
};
