class Solution:
    # Given the box position, it can be moved to a neighbour if
    # 1. The neighbour is free
    # 2. The opposite position of the neighbour is free
    # 3. This opposite position is reachable from S
    # Therefore there are two layers of BFS:
    # A. Box BFS
    # B. Opposite neighbour BFS to currently where S is
    # State is represented by (Sx, Sy, Bx, By)

    def valid_grid_point(self, x, y):
        m, n = len(self.grid), len(self.grid[0])
        return x >= 0 and y >= 0 and x < m and y < n

    def canreach(self, sx, sy, nox, noy):
        dq, visited = deque(), set()
        dq.append((nox, noy))
        visited.add((nox, noy))
        while dq:
            x, y = dq.popleft()
            if x == sx and y == sy:
                return True
            neigs = [(x+1,y),(x-1,y),(x,y+1),(x,y-1)]
            for xn, yn in neigs:
                if self.valid_grid_point(xn, yn) and self.grid[xn][yn] == "." and (xn,yn) not in visited:
                    dq.append((xn,yn))
                    visited.add((xn,yn))
        return False

    def minPushBox(self, grid: List[List[str]]) -> int:
        self.grid = grid
        m, n = len(grid), len(grid[0])
        for i in range(m):
            for j in range(n):
                if self.grid[i][j] == "T":
                    Tx, Ty = i, j
                    self.grid[i][j] = "."
                elif self.grid[i][j] == "S":
                    Sx, Sy = i, j
                    self.grid[i][j] = "."
                elif self.grid[i][j] == "B":
                    Bx, By = i, j
                    self.grid[i][j] = "."

        dq, visited, push = deque(), set(), 0
        dq.append((Sx, Sy, Bx, By))
        visited.add((Sx, Sy, Bx, By))
        while dq:
            for i in range(len(dq)):
                sx, sy, bx, by = dq.popleft()
                if bx == Tx and by == Ty:
                    return push
                neigs = [(bx+1,by), (bx-1,by), (bx, by+1), (bx, by-1)]
                neigs_oppo = [(bx-1,by), (bx+1,by), (bx, by-1), (bx, by+1)]
                for j in range(4):
                    nx, ny = neigs[j]
                    nox, noy = neigs_oppo[j]
                    if self.valid_grid_point(nx, ny) and self.valid_grid_point(nox, noy) and self.grid[nx][ny] == "." and self.grid[nox][noy] == ".":
                        self.grid[bx][by] = "#"
                        if self.canreach(sx, sy, nox, noy) and (bx, by, nx, ny) not in visited:
                            dq.append((bx, by, nx, ny))
                            visited.add((bx, by, nx, ny))                            
                        self.grid[bx][by] = "."
            push += 1
        return -1
