My solution is a double BFS solution. I start from the starting position from of the box and do a BFS to reach the target. The added complexity now is to ensure our player can reach the required position to enforce this change i.e. a push.

```
typedef pair<int,int> P;

struct state{

    P b,p;    
    bool operator <(const state& aa) const {
        return make_pair(b, p) < make_pair(aa.b, aa.p);
    }
};

class Solution {
public:
    vector<int> dx = {1,-1, 0, 0};
    vector<int> dy = {0, 0, 1,-1};

    int M,N;

    P getLocation(vector<vector<char>>& grid, char c){
        for(int i = 0 ; i < M ; i++){
            for(int j = 0 ; j < N ; j++){
                if(grid[i][j] == c){
                    return {i,j};
                }
            }
        }
        return {0,0};
    }

    bool isValid(vector<vector<char>>& grid, int x, int y){
        if(x >= M || x < 0 || y >= N || y < 0 || grid[x][y] == '#')
            return false;
        return true;
    }

    bool canReach(vector<vector<char>>& grid ,P curPos, P endPos, P boxPos){
        // BFS to try reaching a,b from x,y
        queue<pair<int,int>> q;
        q.push(curPos);
        set<P> visited;

        while(!q.empty()){
            int sz = q.size();
            while(sz--){

                P pos = q.front();
                if(pos == endPos)
                    return true;
                q.pop();

                for(int i = 0 ; i < 4; i++){
                    P pos_new = {pos.first + dx[i], pos.second + dy[i]};
                    if(!isValid(grid, pos_new.first, pos_new.second) || visited.count(pos_new))
                        continue;
                    if(pos_new == boxPos)
                        continue;
                    q.push(pos_new);
                    visited.insert(pos_new);
                }
            }
        }

        return false;
    }

    int minPushBox(vector<vector<char>>& grid){
        // grid = GRID;
        M = grid.size();
        N = grid[0].size();

        P tgt = getLocation(grid, 'T');
        P player = getLocation(grid, 'S');
        P box = getLocation(grid, 'B');

        queue<state> q;
        q.push(state{box, player});
        int steps = 0;
        set<state> visited;

        // BFS
        while(!q.empty()){
            int sz = q.size();
            while(sz--){

                auto cur_state = q.front();
                q.pop();
                if(cur_state.b == tgt)
                    return steps;


                for(int i = 0 ; i < 4; i++){
                    int box_new_x = cur_state.b.first + dx[i];
                    int box_new_y = cur_state.b.second + dy[i];

                    // check if box can be pushed to the new lcoation
                    if(!isValid(grid, box_new_x, box_new_y)){
                        continue;
                    }

                    // check if person can push the box
                    int per_new_x = cur_state.b.first - dx[i];
                    int per_new_y = cur_state.b.second - dy[i];

                    // check if box can be pushed to the new lcoation
                    if(!isValid(grid, per_new_x, per_new_y)){
                        continue;
                    }

                    // can person reach the new loc
                    if(!canReach(grid, cur_state.p, {per_new_x, per_new_y} , cur_state.b)){
                        continue;
                    }

                    state new_state = state{{box_new_x,box_new_y},{per_new_x,per_new_y}};
                    if(visited.count(new_state))
                        continue;

                    q.push(new_state);
                    visited.insert(new_state);
                }
            }
            steps++;
        }
        return -1;        
    }
};             
```

