class Solution {
    public int minPushBox(char[][] grid) {
        int row = grid.length, col = grid[0].length;
        int px = -1, py = -1, bx = -1, by = -1, tx = -1, ty = -1;
        Set<Integer> seen = new HashSet<>();
        for(int i = 0; i < row; i++) {
            for(int j = 0; j < col; j++) {
                if(grid[i][j] == 'S') {
                    px = i; py = j;
                    grid[i][j] = '.';
                }
                if(grid[i][j] == 'B') {
                    bx = i; by = j;
                    grid[i][j] = '.';
                }
                if(grid[i][j] == 'T') {
                    tx = i; ty = j;
                }
            }
        }
        Queue<int[]> q = new LinkedList<>();
        q.add(new int[]{px, py, bx, by});
        int ans = 0;
        while(!q.isEmpty()) {
            int size = q.size();
            while(size-- > 0) {
                int[] el = q.poll();
                if(el[2] == tx && el[3] == ty) return ans;
                int en = encode(el);
                if(seen.contains(en)) continue;
                seen.add(en);
                // try push
                for(int[] dir: dirs) {
                    int nbx = el[2] + dir[0], nby = el[3] + dir[1];
                    if(nbx < 0 || nbx >= row || nby < 0 || nby >= col || grid[nbx][nby] == '#') continue;
                    int npx = el[2] - dir[0], npy = el[3] - dir[1];
                    if(npx < 0 || npx >= row || npy < 0 || npy >= col || grid[npx][npy] == '#') continue;
                    if(reach(npx, npy, el[0], el[1], el[2], el[3], grid, row, col)) {
                        int[] nel = new int[4];
                        nel[0] = el[2]; nel[1] = el[3]; nel[2] = nbx; nel[3] = nby;
                        if(!seen.contains(encode(nel)))
                            q.add(nel);
                    }
                }
            }
            ans++;
        }

        return -1;
    }

    boolean reach(int npx, int npy, int px, int py, int bx, int by, char[][] grid, int row, int col) {
        grid[bx][by] = '#';
        Queue<int[]> q = new LinkedList<>();
        Set<Integer> seen = new HashSet<>();
        q.add(new int[]{px, py, 0, 0});
        while(!q.isEmpty()) {
            int[] el = q.poll();
            if(el[0] == npx && el[1] == npy) {
                grid[bx][by] = '.';
                return true;
            }
            if(seen.contains(encode(el))) continue;
            seen.add(encode(el));
            for(int[] dir: dirs) {
                int nx = el[0] + dir[0], ny = el[1] + dir[1];
                int[] nel = new int[]{nx, ny, 0, 0};
                if(nx < 0 || nx >= row || ny < 0 || ny >= col || grid[nx][ny] == '#' || seen.contains(encode(nel))) continue;
                q.add(nel);
            }
        }
        grid[bx][by] = '.';
        return false;
    }

    int[][] dirs = {{1, 0}, {-1, 0}, {0, -1}, {0, 1}};

    int encode(int[] el) {
        return el[0] + 20 * el[1] + 400 * el[2] + 8000 * el[3];
    }
}

