class Solution {
public:
    int temp[50][50];
    vector<pair<int, int>> dots;
    int minPushBox(vector<vector<char>>& grid) {

        int m = grid.size(), n = grid.front().size();
        int start_y, start_x;
        int target_y, target_x;
        int cue_y, cue_x;
        for (int y = 0; y < m; y++){
            for (int x = 0; x < n; x++){
                if (grid[y][x] != '#'){
                    dots.push_back({y, x});
                }
                if (grid[y][x] == 'B'){
                    start_y = y;
                    start_x = x;
                    grid[y][x] = '.';
                }
                if (grid[y][x] == 'T'){
                    target_y = y;
                    target_x = x;
                    grid[y][x] = '.';
                }
                if (grid[y][x] == 'S'){
                    cue_y = y, cue_x = x;
                    grid[y][x] = '.';
                }
            }
        }
        /* the box starts from [start_y, start_x], our aim is [target_y, target_x]
        */
        int nd[4][2] = {{0, -1}, {-1, 0}, {0, 1}, {1, 0}};
        queue<Unit> units; units.push(Unit(cue_y, cue_x, start_y, start_x));
        vector<vector<vector<vector<int>>>> marked(m, vector<vector<vector<int>>>(n, vector<vector<int>>(m, vector<int>(n, INT_MAX))));  
        while(!units.empty()){
            int round_size = units.size();
            for (int i = 0; i < round_size; i++){
                Unit unit = units.front();  units.pop();
                if (unit.by == target_y && unit.bx == target_x){
                    return unit.push;
                }
                // unit.y, unit.x, unit.push
                for (int k = 0; k < 4; k++){
                    int ny = unit.by + nd[k][0], nx = unit.bx + nd[k][1];
                    int py = unit.by - nd[k][0], px = unit.bx - nd[k][1];
                    if (ny >= 0 && ny < m && nx >= 0 && nx < n 
                        && py >= 0 && py < m && px >= 0 && px < n){
                        /*  if we can go to [py, px]->[y, x]->[ny, nx]
                            [cue_y, cue_x] -> [py, px] must be available
                        */
                        if (is_available(grid, m, n, unit.cy, unit.cx, py, px, unit.by, unit.bx)){
                            // printf("available.\n");
                            if (grid[ny][nx] == '.' && grid[py][px] == '.'){
                                int update_push = unit.push + 1;
                                if (update_push < marked[unit.by][unit.bx][ny][nx]){
                                    // printf("move from [y=%d, x=%d] to [ny=%d, nx=%d]\n", unit.y, unit.x, ny, nx);
                                    marked[unit.by][unit.bx][ny][nx] = update_push;
                                    units.push(Unit(unit.by, unit.bx, ny, nx, unit.push + 1));
                                }
                            }                            
                        }
                    }

                }
            }
        }
        return -1;
    }
    bool is_available(vector<vector<char>>& grid, int m, int n, int cy, int cx, int py, int px, int by, int bx){

        int nd[4][2] = {{0, -1}, {-1, 0}, {0, 1}, {1, 0}};
        queue<Unit> units;  units.push(Unit(cy, cx, by, bx));
        for (auto pair: this->dots){
            this->temp[pair.first][pair.second] = INT_MAX;
        }
        this->temp[cy][cx] = 0;
        while(!units.empty()){
            int round_size = units.size();
            for (int i = 0; i < round_size; i++){
                Unit unit = units.front();  units.pop();
                if (unit.cy == py && unit.cx == px){
                    return true;
                }
                for (int k = 0; k < 4; k++){
                    int ny = unit.cy + nd[k][0], nx = unit.cx + nd[k][1];
                    if (ny >= 0 && ny < m && nx >= 0 && nx < n){
                        if (grid[ny][nx] == '.' && (ny != by || nx != bx)){
                            int update_push = unit.push + 1;
                            if (update_push < this->temp[ny][nx]){
                                this->temp[ny][nx] = update_push;
                                units.push(Unit(ny, nx, by, bx, update_push));
                            }
                        }
                    }
                }
            }
        }
        return false;
    }
};
