class Solution:
    def minPushBox(self, grid: List[List[str]]) -> int:
        m, n = len(grid), len(grid[0])
        for i in range(m):
            for j in range(n):
                if grid[i][j] == "T":
                    target = (i,j)
                if grid[i][j] == "B":
                    box = (i,j)
                if grid[i][j] == "S":
                    person = (i,j)

        def valid(pos) -> bool:
            nonlocal grid, m, n
            return 0<=pos[0]<m and 0<=pos[1]<n and grid[pos[0]][pos[1]]!='#'

        def manhattan_dist(p1, p2) -> int:
            return abs(p1[0]-p2[0]) +abs(p1[1]-p2[1])

        def check(s, d, box) -> bool:
            open_set = [(manhattan_dist(s, d), 0, s)]
            vis = {s, box}
            while open_set:
                _, dist, pos = heapq.heappop(open_set)
                if pos == d:
                    return True
                for new_pos in [(pos[0]+1, pos[1]), (pos[0]-1, pos[1]), (pos[0], pos[1]+1), (pos[0], pos[1]-1)]:
                    if valid(new_pos) and new_pos not in vis:
                        vis.add(new_pos)
