class Solution {
    map<vector<int>, bool> beenhere;
    bool visited(int x, int y, int px, int py) {
        return beenhere.find({x, y, px, py}) != beenhere.end();
    }
    bool dfs(vector<vector<char>>& g, int tx, int ty, int sx, int sy, int bx, int by, vector<vector<bool>>& vis) {
        if (sx < 0 || sy < 0 || sx == g.size() || sy == g[0].size())
            return false;
        if (g[sx][sy] ==  '#' || vis[sx][sy] || sx == bx && sy == by)
            return false;
        if (sx == tx && sy == ty)
            return true;
        vis[sx][sy] = true;
        return dfs(g, tx, ty, sx - 1, sy, bx, by, vis) ||
               dfs(g, tx, ty, sx + 1, sy, bx, by, vis) ||
               dfs(g, tx, ty, sx, sy - 1, bx, by, vis) ||
               dfs(g, tx, ty, sx, sy + 1, bx, by, vis);
    }
    bool canreach(vector<vector<char>>& g, int tx, int ty, int sx, int sy, int bx, int by) {
        vector<vector<bool>> vis(g.size(), vector<bool>(g[0].size(), false));
        return dfs(g, tx, ty, sx, sy, bx, by, vis);
    }
public:
    int minPushBox(vector<vector<char>>& g) {
        int m = g.size();
        int n = g[0].size();
        int px = -1, py = -1, bx = -1, by = -1;
        for (int i = 0; i < m; i++) {
            for (int j = 0; j < n; j++) {
                if (g[i][j] == 'B') {
                    bx = i;
                    by = j;
                } else if (g[i][j] == 'S') {
                    px = i;
                    py = j;
                }
                if (px != -1 && bx != -1)
                    break;
            }
        }
        queue<tuple<int, int, int, int>> q;
        q.push({bx, by, px, py});
        int moves = 0;
        while (q.size()) {
            int size = q.size();
            while (size--) {
                auto[x, y, px, py] = q.front();
                q.pop();
                if (g[x][y] == 'T')
                    return moves;
                if (x > 0 && x < m - 1 && g[x - 1][y] != '#' && g[x + 1][y] != '#') {
                    if (!visited(x - 1, y, x + 1, y) && canreach(g, x + 1, y, px, py, x, y)) {
                        beenhere[{x - 1, y, x + 1, y}] = true;
                        q.push({x - 1, y, x, y});
                    }
                    if (!visited(x + 1, y, x - 1, y) && canreach(g, x - 1, y, px, py, x, y)) {
                        beenhere[{x + 1, y, x - 1, y}] = true;
                        q.push({x + 1, y, x, y});
                    }
                }
                if (y > 0 && y < n - 1 && g[x][y - 1] != '#' && g[x][y + 1] != '#') {
                    if (!visited(x, y - 1, x, y + 1) && canreach(g, x, y + 1, px, py, x, y)) {
                        beenhere[{x, y - 1, x, y + 1}] = true;
                        q.push({x, y - 1, x, y});
                    }
                    if (!visited(x, y + 1, x, y - 1) && canreach(g, x, y - 1, px, py, x, y)) {
                        beenhere[{x, y + 1, x, y - 1}] = true;
                        q.push({x, y + 1, x, y});
                    }
                }
            }
            moves++;
        }
        return -1;
    }
};
