        box_visited = set()
        for i in range(m):
            for j in range(n):
                if grid[i][j] == 'B':
                    b_pos = (i,j)
                elif grid[i][j] == 'S':
                    s_pos = (i,j)
                elif grid[i][j] == 'T':
                    t_pos = (i,j)
        que = deque([(b_pos,s_pos,0)])
        while que:
            (bi,bj),(si,sj),d = que.popleft()
