        m, n = len(grid),len(grid[0])
        def dfs(i,j,ti,tj,grid,visited):
            if (i,j) == (ti,tj): return True
            if (i,j) in visited:
                return False
            visited.add((i,j))
            res = False
            for di,dj in [[-1,0],[1,0],[0,-1],[0,1]]:
                if 0<=i+di<m and 0<=j+dj<n and grid[i+di][j+dj] in [".","T"]:
                    res = res or dfs(i+di,j+dj,ti,tj,grid,visited)
            return res
        box_visited = set()
        for i in range(m):
            for j in range(n):
                if grid[i][j] == 'B':
                    b_pos = (i,j)
                elif grid[i][j] == 'S':
                    s_pos = (i,j)
                elif grid[i][j] == 'T':
                    t_pos = (i,j)
        que = deque([(b_pos,s_pos,0)])
        while que:
            (bi,bj),(si,sj),d = que.popleft()
            if (bi,bj) == t_pos: return d
            if (bi,bj,si,sj) in box_visited: continue
            box_visited.add((bi,bj,si,sj))
            tmp_grid = copy.deepcopy(grid)
            tmp_grid[b_pos[0]][b_pos[1]] = '.'
            tmp_grid[s_pos[0]][s_pos[1]] = '.'
            tmp_grid[bi][bj] = 'B'
            for di,dj in [[-1,0],[1,0],[0,-1],[0,1]]:
                if 0<=bi+di<m and 0<=bj+dj<n and 0<=bi-di<m and 0<=bj-dj<n:
                    if tmp_grid[bi-di][bj-dj] in [".","S","T"] and tmp_grid[bi+di][bj+dj] in [".","S","T"] and dfs(si,sj,bi+di,bj+dj,tmp_grid,set()):
                        que.append(((bi-di,bj-dj),(bi,bj),d+1))
        return -1

