type Coord struct{
    x int
    y int
}

func (this *Coord)equal(comp Coord) bool {
    return this.x == comp.x && this.y == comp.y
}

func (this *Coord) coordExistIn(list []Coord) bool {
    for _, v := range(list) {
        if this.equal(v) {
            return true
        }
    }
    return false
}

type State struct {
    box Coord
    player Coord
    moves int
}

type StateList []State

func (this StateList) Len() int { return len(this) }
func (this StateList) Less(i, j int) bool { return (this)[i].moves < (this)[j].moves }
func (this StateList) Swap(i, j int) { (this)[i], (this)[j] = (this)[j], (this)[i] }
func (this *StateList) Push(x interface{}) {
    *this = append(*this, x.(State))
}
func (this *StateList) Pop() interface{} {
    old := *this
	n := len(old)
	x := old[n-1]
	*this = old[0:n-1]
	return x
}

type StateCoord struct {
    box Coord
    player Coord
}

func (this *StateCoord) existIn(list []StateCoord) bool {
    for _, v := range(list) {
        if this.box.equal(v.box) && this.player.equal(v.player) {
            return true
        }
    }
    return false
}

func minPushBox(grid [][]byte) int {
    player := Coord{}
    box := Coord{}
    target := Coord{}
    for j:=0; j< len(grid[0]); j++ {
        for i:=0; i<len(grid); i++ {
            switch grid[i][j] {
                case 'B': box = Coord{x: i, y: j}
                case 'S': player = Coord{x: i, y: j}
                case 'T': target = Coord{x: i, y: j}
            }
        }
    }

    stack := &StateList{}
    visited := []StateCoord{}
    heap.Init(stack)

    heap.Push(stack, State{box, player, 0})

    for len(*stack) > 0 {

        state := heap.Pop(stack).(State)
        box := state.box
        player := state.player
        moves := state.moves

        visited = append(visited, StateCoord{box,player})

        if box.equal(target){
            return moves
        }

        // Try to push left 
        stateCoord := StateCoord{Coord{box.x, box.y-1},Coord{player.x, player.y-1}}
        if box.y-1 >=0 && box.x == player.x && box.y+1 == player.y && grid[box.x][box.y-1] != '#' && !stateCoord.existIn(visited){
            heap.Push(stack, State{Coord{box.x, box.y-1},Coord{player.x, player.y-1}, moves+1})    
        }
        // Try to push right
        stateCoord = StateCoord{Coord{box.x, box.y+1},Coord{player.x, player.y+1}}
        if box.y+1 < len(grid[0]) && box.x == player.x && box.y-1 == player.y && grid[box.x][box.y+1] != '#' && !stateCoord.existIn(visited){
            heap.Push(stack, State{Coord{box.x, box.y+1},Coord{player.x, player.y+1}, moves+1})    
        }
        // Try to push up
        stateCoord = StateCoord{Coord{box.x-1, box.y},Coord{player.x-1, player.y}}
        if box.x-1 >=0 && box.x+1 == player.x && box.y == player.y && grid[box.x-1][box.y] != '#' && !stateCoord.existIn(visited){
            heap.Push(stack, State{Coord{box.x-1, box.y},Coord{player.x-1, player.y}, moves+1})    
        }
        // Try to push down
        stateCoord = StateCoord{Coord{box.x+1, box.y},Coord{player.x+1, player.y}}
        if box.x+1 < len(grid) && box.x-1 == player.x && box.y == player.y && grid[box.x+1][box.y] != '#' && !stateCoord.existIn(visited){
            heap.Push(stack, State{Coord{box.x+1, box.y},Coord{player.x+1, player.y}, moves+1})    
        }

        for _, newCoord := range([]Coord{{box.x,box.y-1}, {box.x, box.y+1}, {box.x-1,box.y}, {box.x+1,box.y}}) {
            stateCoord = StateCoord{box,newCoord}
            if isValidCoordinates(newCoord, grid) && !stateCoord.existIn(visited) && canMoveTo(player, newCoord, box, grid) {
                heap.Push(stack, State{box,newCoord, moves})        
            }
        }

    }

    return -1
}

func canMoveTo(start, end, box Coord, grid [][]byte) bool {
    visited := []Coord{}
    stack := []Coord{start}

    for len(stack)>0 {
        coord := stack[0]
        stack = stack[1:]

        if coord.equal(end) {
            return true
        }

        visited = append(visited, coord)

        for _, new_coord := range([]Coord{{coord.x,coord.y-1}, {coord.x, coord.y+1}, {coord.x-1,coord.y}, {coord.x+1,coord.y}}) {
            if isValidCoordinates(new_coord, grid) && !new_coord.equal(box) && !new_coord.coordExistIn(visited) && !new_coord.coordExistIn(stack) {
                stack = append(stack, new_coord)
            }
        }
    }
    return false

}

func isValidCoordinates(coord Coord, grid [][]byte) bool{
    if 0 <= coord.x && coord.x < len(grid) && 0 <= coord.y && coord.y < len(grid[0]) && grid[coord.x][coord.y] != '#'{
        return true
    }
    return false
}
