class Solution {
    vector<int> dirs = {-1,0,1,0,-1};
    int m,n;
public:
    int minPushBox(vector<vector<char>>& grid) {
        m = grid.size();
        n = grid[0].size();
        int si,sj,ei,ej,bi,bj;
        for (int i=0; i<m; i++) {
            for (int j=0; j<n; j++) {
                if (grid[i][j]=='S') {
                    si = i;
                    sj = j;
                }
                if (grid[i][j]=='B') {
                    bi = i;
                    bj = j;
                }
                if (grid[i][j]=='T') {
                    ei = i;
                    ej = j;
                }
            }
        }
        auto compare = [&ei,&ej](vector<int>&a, vector<int>&b) {
            return abs(a[0]-ei) + abs(a[1]-ej) + a[4] > abs(b[0]-ei) + abs(b[1]-ej) + b[4];    
        };
        priority_queue<vector<int>, vector<vector<int>>, decltype(compare)> pq(compare);
        pq.push({bi,bj,si,sj,0});
        unordered_set<string> visited;
        visited.insert(help({bi,bj,si,sj}));
        while (!pq.empty()) {
            auto cur = pq.top();
            pq.pop();
            int cbi = cur[0], cbj = cur[1], i = cur[2], j = cur[3], cost = cur[4];
            for (int k=0; k<4; k++) {
                int ii = i+dirs[k], jj = j+dirs[k+1];
                if (ii>=0 && jj>=0 && ii<m && jj<n && grid[ii][jj]!='#' && visited.find(help({cbi,cbj,ii,jj}))==visited.end()) {
                    if (ii==cbi && jj==cbj) {
                        int cbii = cbi+dirs[k], cbjj = cbj+dirs[k+1];
                        if (cbii==ei && cbjj==ej)   return cost+1;
                        if (cbii>=0 && cbjj>=0 && cbii<m && cbjj<n && grid[cbii][cbjj]!='#') {
                            pq.push({cbii,cbjj,ii,jj,cost+1});
                        }
                    } else {
                        visited.insert(help({cbi,cbj,ii,jj}));
                        pq.push({cbi,cbj,ii,jj,cost});
                    }
                }
            }
        }
        return -1;
    }

    string help(vector<int> a) {
        string res = "";
        for (int i=0; i<4; i++)
            res+=to_string(a[i])+",";
        return res;
    }
};
