namespace lc1263_push_box {
// 1263. Minimum Moves to Move a Box to Their Target Location
class Solution {
public:
    // BFS over a derived graph, derived from the input grid --
    // each vertex in the derived graph has two states:
    // * the Storekeeper/Player p{x,y} and
    // * the Box location b{x,y}.
    //
    // A push/move of the box is feasible if
    // 1. the storekeeper can go to the position behind the box
    // (this will be determined using another "nested" BFS. Time complexity of this "nested" BFS:
    // O(R) where R is the number of reachable cells by the storekeeper/player.).
    // 2. the box's new location is empty.
    //
    // Overal Time-Complexity: O(R*R) since the main BFS queue may contain up to 4*R items, and each
    // item may take O(R) time to determine whether player can reach the ideal position to push the
    // box.
    //
    /// we can use a 4-tuple to represent the state:
    /// {xBox, yBox, xPlayer, yPlayer}.
    /// Note that, except the initial state, the player is always adjacent to the box in the states.
    /// so the possibility of the states are at most 4*R where R is the cells that can be reached.
    using State = tuple<int8_t, int8_t, int8_t, int8_t>;
    using Position = tuple<int8_t, int8_t>;

    // deltas for 4 directions to which the box can be pushed.
    constexpr static const array<int8_t, 4> RowDelta{1, -1, 0, 0};
    constexpr static const array<int8_t, 4> ColDelta{0, 0, 1, -1};

    // using C++17 structured binding to extract the parts out of the State:
    // auto [boxX, boxY, playerX, playerY] = state;
    int minPushBox(vector<vector<char>>& grid) {
        // 1 <= m,n <= 20. Thus a byte can represent a {x,y} pair if we use bit-fields.
        // but for saving time, we won't do that.
        const int8_t m = grid.size(), n = grid[0].size();

        // extract the positions of the player and the box, and the target
        Position player, box, target;
        extractPositions(grid, player, box, target);

        // the queue for the main BFS.
        queue<State> Q;
        unordered_set<uint32_t> visited;

        int numPushes = 0;
        const auto [xTarget, yTarget] = target;
        // enqueue the initial states
        {
            State initialState = std::tuple_cat(box, player);
            Q.emplace(initialState);
            visited.emplace(stateToUInt32(initialState));
        }

        // main body of the BFS
        while (!Q.empty()) { // x for row, y for col
            auto qSize = Q.size();
            ++numPushes;
            while (qSize--) {
                const auto [xBox, yBox, xPlayer, yPlayer] = Q.front();
                Q.pop();
                if (xBox == xTarget && yBox == yTarget) {
                    return numPushes - 1; // got a good solution
                }
                // optimization: compute the reachability only once for 4 neighboring cells.
                auto reachableCells = computeReachableCells(grid, {xBox, yBox}, {xPlayer, yPlayer});
                for (int i = 0; i < RowDelta.size(); ++i) { // exploring the 4 neighbor cells
                    int8_t newXBox = xBox + RowDelta[i];
                    int8_t newYBox = yBox + ColDelta[i];
                    int8_t idealXPlayer = xBox - RowDelta[i];
                    int8_t idealYPlayer = yBox - ColDelta[i];
                    // note that {xBox, yBox} is the new position of the player/storekeeper
                    State newState = {newXBox, newYBox, xBox, yBox};
                    if (withinBounds(m, n, newXBox, newYBox) // pushable
                        && isEmptyCell(grid, newXBox, newYBox)
                        && withinBounds(m, n, idealXPlayer, idealYPlayer) //
                        && isEmptyCell(grid, idealXPlayer, idealYPlayer)
                        // player/storekeeper can reach it
                        && reachableCells[idealXPlayer][idealYPlayer]
                        // the new state is not visited yet.
                        && visited.count(stateToUInt32(newState)) == 0) {
                        // enqueue the new state, and mark it as visited.
                        Q.emplace(newState);
                        visited.emplace(stateToUInt32(newState));
                    }
                }
            } // qSize != 0
        } // q not empty

        return -1;
    }

private:
    static inline bool isEmptyCell(const vector<vector<char>>& grid, int8_t row, int8_t col) {
        return grid[row][col] == '.';
    }

    static inline bool withinBounds(int8_t m, int8_t n, int8_t row, int8_t col) {
        return 0 <= row && row < m && 0 <= col && col < n;
    }

    static inline uint32_t stateToUInt32(State state) {
        const auto [a, b, c, d] = state;
        uint32_t res = ((uint32_t)a << 24) | ((uint32_t)b << 16) | ((uint32_t)c << 8) | (uint32_t)d;
        return res;
    }

    /// use BFS to compute the cells that the player can reach
    static vector<vector<bool>>
    computeReachableCells(vector<vector<char>>& grid, Position box, Position player) {
        const int8_t m = grid.size(), n = grid[0].size();
        const auto [xBox, yBox] = box;
        const auto [xPlayer, yPlayer] = player;
        assert(isEmptyCell(grid, xBox, yBox));
        assert(isEmptyCell(grid, xPlayer, yPlayer));
        // mark the box's and the storekeeper/player's positions in the grid.
        // it'll be restored just before exit.
        grid[xBox][yBox] = 'B';
        grid[xPlayer][yPlayer] = 'S';
        // R is the both the reachability matrix and the visited-ness matrix
        vector<vector<bool>> R(m, vector<bool>(n, false));
        queue<Position> Q;
        R[xPlayer][yPlayer] = true;
        Q.emplace(player);
        while (!Q.empty()) {
            const auto [xpos, ypos] = Q.front();
            Q.pop();
            for (int i = 0; i < RowDelta.size(); ++i) {
                int8_t newXPos = xpos + RowDelta[i];
                int8_t newYPos = ypos + ColDelta[i];
                if (withinBounds(m, n, newXPos, newYPos) //
                    && isEmptyCell(grid, newXPos, newYPos)
                    && !R[newXPos][newYPos] // not already visited/reached
                ) {
                    R[newXPos][newYPos] = true;
                    Q.emplace(newXPos, newYPos);
                }
            }
        }
        // restore the grid
        grid[xBox][yBox] = '.';
        grid[xPlayer][yPlayer] = '.';

        return R;
    }

    static void extractPositions(
            vector<vector<char>>& grid,
            Position& player,
            Position& box,
            Position& target) {
        const int m = grid.size(), n = grid[0].size();

        for (int8_t r = 0; r < m; ++r) {
            for (int8_t c = 0; c < n; ++c) {
                if (grid[r][c] == 'S') {
                    player = {r, c};
                    grid[r][c] = '.';
                } else if (grid[r][c] == 'B') {
                    box = {r, c};
                    grid[r][c] = '.';
                } else if (grid[r][c] == 'T') {
