    def minPushBox(self, grid: List[List[str]]) -> int:
        m, n = len(grid), len(grid[0])

        # get target, box, person
        for i in range(m):
            for j in range(n):
                if grid[i][j] == "T":
                    target = (i,j)
                if grid[i][j] == "B":
                    box = (i,j)
                if grid[i][j] == "S":
                    person = (i,j)

        # 
        def valid(x,y):
            return 0 <= x < m and 0 <= y < n and grid[x][y] != "#"

        # heuristic
        def heuristic(box):
            return abs(box[0]-target[0]) + abs(box[1]-target[1])


        # check person can go to the position to push box
        def check(cur,dist,box):
            q = deque([cur])
            visited = {cur}
            while q:
                pos = q.popleft()
                if pos == dist:
                    return True
                nex = [(pos[0]+1, pos[1]),(pos[0]-1, pos[1]),(pos[0], pos[1]+1),(pos[0], pos[1]-1)]
                for x, y in nex:
                    if valid(x,y) and (x,y) not in visited and (x,y) != box:
                        q.append((x,y))
                        visited.add((x,y))
            return False


        # bfs
        #q = deque([(0,box,person)])
        pq = [(heuristic(box),0,box,person)]
        seen = {(box,person)}
        #while q:
        while pq:
            #ans, b, p = q.popleft()
            _, ans, b, p = heapq.heappop(pq)
            if b == target:
                return ans

            for di, dj in [(1,0),(0,1),(-1,0),(0,-1)]:
                b_nxt = (b[0]+di, b[1]+dj)
                p_nxt = (b[0]-di, b[1]-dj)

                if valid(*b_nxt) and (b_nxt,p_nxt) not in seen:
