class Solution {
public:
    string get4(int a, int b, int c, int d){  
        return to_string(a) + "#" + to_string(b) + "#" + to_string(c) + "#" + to_string(d);
    }
    int minPushBox(vector<vector<char>>& grid) {
        pair<int,int> b,p;
       for(int i=0;i<grid.size();i++){
           for(int j=0;j<grid[0].size();j++){
               if(grid[i][j]=='S')p={i,j};
               if(grid[i][j]=='B'){
                   grid[i][j]='.';
                   b={i,j};
               }
           }
       }
        unordered_set<string> s;
        queue<vector<int>> q;
        q.push({b.first,b.second,p.first,p.second});
        int ans=0;
        while(!q.empty()){
            int sz=q.size();
            while(sz--){
               int i=q.front()[0];          // mat[i][j] is the current position of a Box
                int j=q.front()[1];
                int pi=q.front()[2];        //mat[pi][pj] is the current position of a person
                int pj=q.front()[3];
                q.pop();
                if(grid[i][j]=='T')return ans;
                if(i-1>=0&&grid[i-1][j]!='#'&&s.count(get4(i,j,i+1,j))==0&&ispos(grid,i,j,i+1,j,pi,pj)){
                    s.insert(get4(i,j,i+1,j));
                    q.push({i-1,j,i+1,j});
                }
                if(i+1<grid.size()&&grid[i+1][j]!='#'&&s.count(get4(i,j,i-1,j))==0&&ispos(grid,i,j,i-1,j,pi,pj)){
                    s.insert(get4(i,j,i-1,j));
                    q.push({i+1,j,i-1,j});
                }
                if(j-1>=0&&grid[i][j-1]!='#'&&s.count(get4(i,j,i,j+1))==0&&ispos(grid,i,j,i,j+1,pi,pj)){
                   s.insert(get4(i,j,i,j+1));
                    q.push({i,j-1,i,j+1});
                }
                if(j+1<grid[0].size()&&grid[i][j+1]!='#'&&s.count(get4(i,j,i,j-1))==0&&ispos(grid,i,j,i,j-1,pi,pj)){
                    s.insert(get4(i,j,i,j-1));
                    q.push({i,j+1,i,j-1});
                }
            }
            ans++;
        }
        return -1;
    }
    bool ispos(vector<vector<char>>&nums,int bi, int bj, int fi,int fj,int ii,int ij){
        if(fi<0||fi>=nums.size()||fj<0||fj>=nums[0].size())return false;
        if(fi==ii&&fj==ij)return true;
        char c=nums[bi][bj];
        nums[bi][bj]='#';
        queue<pair<int,int>> q;
        q.push({ii,ij});
        vector<vector<int>> visited(nums.size(),vector<int>(nums[0].size(),0));
        while(!q.empty()){
            int s=q.size();
            while(s--){
                int i=q.front().first;
                int j=q.front().second;
                if(i==fi&&j==fj){
                    nums[bi][bj]=c;
                    return true;
                }
                q.pop();
                if(i-1>=0&&visited[i-1][j]==0&&nums[i-1][j]!='#'){
                    visited[i-1][j]=1;
                    q.push({i-1,j});
                }
                if(i+1<nums.size()&&visited[i+1][j]==0&&nums[i+1][j]!='#'){
                    visited[i+1][j]=1;
                    q.push({i+1,j});
                }
                if(j-1>=0&&visited[i][j-1]==0&&nums[i][j-1]!='#'){
                    visited[i][j-1]=1;
                    q.push({i,j-1});
                }
                if(j+1<nums[0].size()&&visited[i][j+1]==0&&nums[i][j+1]!='#'){
                    visited[i][j+1]=1;
                    q.push({i,j+1});
                }
            }
        }
        nums[bi][bj]=c;
        return false;
    }
};

