class Solution {
public:
    struct Status {
        int mr;
        int mc;
        int mf; // 0 = up, 1 = left, 2 = down, 3 = right
        int br;
        int bc;
        int cnt;
    };

    void pushInitStatus(queue<Status>& qu, vector<vector<char>>& grid,
                        int rows, int cols, unordered_map<long long, int>& mp) {
        Status s;
        s.cnt = 0;
        s.mf = 0;
        for (int i = 0; i < rows; ++i) {
            for (int j = 0; j < cols; ++j) {
                if (grid[i][j] == 'B') {
                    s.br = i;
                    s.bc = j;
                } else if (grid[i][j] == 'S') {
                    s.mr = i;
                    s.mc = j;
                }
            }
        }
        long long hash = (long long)(s.br + 1) * 100000000L
                            + (long long)(s.bc + 1) * 1000000
                            + (s.mr + 1) * 10000 + (s.mc + 1) * 100
                            + s.mf + 1;
        mp[hash] = 0;
        qu.push(s);
        for (int i = 0; i < 3; ++i) {
            s.mf++;
            hash++;
            mp[hash] = 0;
            qu.push(s);
        }
    }

    int getManWithBoxFace(Status& s) {
        if (s.mr == s.br + 1 && s.mc == s.bc) {
            return 0;
        }
        if (s.mr == s.br && s.mc == s.bc + 1) {
            return 1;
        }
        if (s.mr == s.br - 1 && s.mc == s.bc) {
            return 2;
        }
        if (s.mr == s.br && s.mc == s.bc - 1) {
            return 3;
        }
        return -1;
    }

    void expand(queue<Status>& qu, vector<vector<char>>& grid, int rows, int cols,
                unordered_map<long long, int>& mp, int dirs[4][2], Status& prev, int pos, bool manWithBox) {
        Status next;
        next.cnt = prev.cnt;
        next.mf = pos;
        next.mr = prev.mr + dirs[pos][0];
        next.mc = prev.mc + dirs[pos][1];
        next.br = prev.br;
        next.bc = prev.bc;
        if (manWithBox) {
            next.br = next.br + dirs[pos][0];
            next.bc = next.bc + dirs[pos][1];
            next.cnt++;
        }
        if (next.mr >= 0 && next.mr < rows && next.mc >= 0 && next.mc < cols && next.br >= 0 
                && next.br < rows && next.bc >= 0 && next.bc < cols) {
            if (grid[next.mr][next.mc] != '#' && grid[next.br][next.bc] != '#'
                    && (next.mr != next.br || next.mc != next.bc)) {
                long long hash = (long long)(next.br + 1) * 100000000L 
                                    + (long long)(next.bc + 1) * 1000000
                                    + (next.mr + 1) * 10000 + (next.mc + 1) * 100 
                                    + next.mf + 1;
                if (!mp.count(hash) || mp[hash] > next.cnt) {
                    mp[hash] = next.cnt;
                    qu.push(next);
                }
            }
        }
    }

    int minPushBox(vector<vector<char>>& grid) {
        int rows = grid.size();
        int cols = grid[0].size();
        queue<Status> qu;
        int dirs[4][2] = {{-1, 0}, {0, -1}, {1, 0}, {0, 1}};
        unordered_map<long long, int> mp;
        pushInitStatus(qu, grid, rows, cols, mp);
        int ans = INT_MAX;
        while (!qu.empty()) {
            Status prev = qu.front();
            qu.pop();
            if (grid[prev.br][prev.bc] == 'T') {
                if (ans > prev.cnt) {
                    ans = prev.cnt;
                }
                continue;
            }
            int manWithBoxFace = getManWithBoxFace(prev);
            if (manWithBoxFace != -1) {
                expand(qu, grid, rows, cols, mp, dirs, prev, manWithBoxFace, true);   // expand most possible first, but still cannot guarantee the first reached T is the shortest.
            }
            for (int i = 0; i < 4; ++i) {
                if (i != manWithBoxFace) {
                    expand(qu, grid, rows, cols, mp, dirs, prev, i, false);
                }
            }
        }
        return ans == INT_MAX ? -1 : ans;
    }
};
