solutions.content:
A classic BFS problem , with new constraints for the player to reach the right pos from its current pos so it can push the box.    Note that a box and pass a same position twice. The first time to make room to let the player pass.  So the visited[] flag needs the combination of box + player positions as a single key. 

```
class Solution {


    int row, col; 

    // find initial position of player S, box B and Target T
    void initialScan (vector<vector<char>>& grid, pair<int,int>& playerPos,   pair<int,int>& boxPos,  pair<int,int>& targetPos)
    {
        for (int i=0; i<row; i++)
            for (int j=0; j<col; j++)
            {
                if (grid[i][j]=='S')
                    playerPos={i,j};
                else if (grid[i][j]=='B')
                    boxPos={i,j};
                else if (grid[i][j]=='T')
                    targetPos={i,j};
            }
    }
    //Check if a coordinate is valid pos to move a box or person into
    bool validPos(vector<vector<char>>& grid, int x, int y) 
    {  // it can be S, T, and B: they change values
        return x>=0 && x<row && y>=0 && y<col && grid[x][y]!='#'; 
    }    
    // check if a person can reach an end pos from a start pos    
    bool reacheableByPerson (vector<vector<char>>& grid, pair<int,int>boxPos, pair<int,int> start_pos, pair<int,int> end_pos)
    {
        // Using BFS, consider current box pos, which can block the way
        queue<pair<int,int>> q; // push, pop, front
        // more efficient, a visited flag: if a pos has been pushed into the queue
        unordered_map <int, unordered_map<int, bool>> visited; 

        q.push(start_pos); 
        visited[start_pos.first][start_pos.second]=true; 
        while (q.size())
        {
            int sz=q.size(); 
            while (sz--)
            {
                auto [a,b] = q.front(); 
                q.pop(); 

                if (a==end_pos.first && b== end_pos.second)
                    return true; 

                // 4 directions
                vector <pair<int,int>> dirs = {{0,1}, {0,-1}, {1,0}, {-1,0}}; 
                for (auto d: dirs)
                {
                    pair<int,int> nextPos= {a+d.first, b+d.second}; 
                    if (validPos(grid, nextPos.first, nextPos.second) 
                         && !visited[nextPos.first][nextPos.second]
                       && nextPos !=boxPos)
                    {
                        q.push (nextPos);
                        visited[nextPos.first][nextPos.second]=true; 
                    }
                }                
            }
        }        
        return false; 
    }

    int linearize(pair<int,int> pos)
    {
        return pos.first * col + pos.second; 
    }
public:
    int minPushBox(vector<vector<char>>& grid) {
        row=grid.size();
        if (row==0)
            return -1; 
        col=grid[0].size(); 

        pair<int,int> playerPos, boxPos, targetPos; 
        initialScan (grid, playerPos, boxPos, targetPos);

        // now focus on the box, do the BFS 
        // must also store current person pos??  box and person
        queue< vector<pair<int,int>> > q; 
        // HUGE MISTAKE:  a box may away from one place to leave room for person to pass, then move back to an old place!!
        // unordered_map <int, unordered_map<int, bool>> visited; 
        // without this : timeout
        // A compromise: the combination of box and person position!!
        unordered_map <int, unordered_map<int, bool>> visited; 

        vector<pair<int,int>> node;
        node.push_back(boxPos); node.push_back(playerPos);
        q.push(node); 
        visited[linearize(boxPos)][linearize(playerPos)]=true; 
        int moveCount=0; 
        while (q.size())
        {
            int sz=q.size(); 
            while (sz--)
            {
                vector<pair<int,int>> cur_node = q.front(); 
                q.pop(); 
                boxPos= cur_node[0];
                playerPos= cur_node[1];

                if (boxPos==targetPos)
                    return moveCount; 

                // 4 directions
                vector <pair<int,int>> dirs = {{0,1}, {0,-1}, {1,0}, {-1,0}}; 
                // 4 opposite directions? 
                for (auto d: dirs)
                {
                    pair<int, int> newPlayerPos= boxPos; 
                    if (d.first==0)
                        newPlayerPos.second-= d.second; 
                    else  if (d.second==0)
                        newPlayerPos.first-= d.first; 

                    pair<int,int> nextPos= {boxPos.first +d.first, boxPos.second + d.second}; 
                    if (validPos(grid, nextPos.first, nextPos.second) 
                        && !visited[linearize(nextPos)][linearize(playerPos)]
                       && reacheableByPerson(grid, boxPos, playerPos, newPlayerPos) )
                    {
                        vector<pair<int,int>> next_node; 
                        next_node.push_back(nextPos);
                        next_node.push_back(newPlayerPos);

                        q.push (next_node);
                        visited[linearize(nextPos)][linearize(playerPos)]=true; 
                    }
                }                
            }
            moveCount++; 
        }        
        return -1;         
    }
};

