    unordered_set<int> visited;
    vector<Point> reachablePoints;
    inline static constexpr int8_t dirs[4][2] = {{-1, 0}, {1, 0}, {0, -1}, {0, 1}};

    inline static int uniqueId(int16_t player, int16_t box)
    {
        return (player << 16) | box;
    }

    void findPushableLocs(const vector<bool>& visitable, int8_t x, int8_t y, unordered_set<int16_t>& dest)
    {
        int16_t box = x * m + y;
        if (x - 1 >= 0 && x + 1 < n && visitable[box - m] && visitable[box + m]) {
            int16_t p = box - m;
            if (visited.find(uniqueId(p, box)) == visited.end())
                dest.insert(p);
            p = box + m;
            if (visited.find(uniqueId(p, box)) == visited.end())
                dest.insert(p);
        }
        if (y - 1 >= 0 && y + 1 < m && visitable[box - 1] && visitable[box + 1]) {
            int16_t p = box - 1;
            if (visited.find(uniqueId(p, box)) == visited.end())
                dest.insert(p);
            p = box + 1;
            if (visited.find(uniqueId(p, box)) == visited.end())
                dest.insert(p);
        }
    }

    void completeObjective(const vector<bool>& visitable, Objective& obj)
    {
        unordered_set<int16_t> dest;
        findPushableLocs(visitable, obj.box.x, obj.box.y, dest);

        if (auto it = dest.find(obj.player.x * m + obj.player.y); it != dest.end()) {
            reachablePoints.emplace_back(obj.player);
            dest.erase(it);
        }
        if (dest.empty())
            return;

        queue<Point> moveQ;
        moveQ.emplace(obj.player);
        vector<bool> playerNotVisited(visitable);
        playerNotVisited[m * obj.player.x + obj.player.y] = false;
        playerNotVisited[m * obj.box.x + obj.box.y] = false;

        while (!moveQ.empty()) {
            for (int s = moveQ.size(); s; --s) {
                auto p = moveQ.front();
                moveQ.pop();
                for (auto& dir : dirs) {
                    Point newP(p.x + dir[0], p.y + dir[1]);
                    int16_t loc = newP.x * m + newP.y;
                    if (newP.x >= 0 && newP.x < n && newP.y >= 0 && newP.y < m && playerNotVisited[loc]) {
                        playerNotVisited[loc] = false;
                        moveQ.emplace(newP);
                        if (auto it = dest.find(loc); it != dest.end()) {
                            reachablePoints.emplace_back(newP);
                            dest.erase(it);
                            if (dest.empty())
                                return;
                        }
                    }
                }
            }
        }
    }

public:
    int minPushBox(vector<vector<char>>& grid)
    {
        n = grid.size();
        m = grid[0].size();
        vector<bool> visitable(n * m, true);
        Point player{-1, -1}, finish{-1, -1}, box{-1, -1};
        for (int8_t i = 0; i < n; ++i) {
            for (int8_t j = 0; j < m; ++j) {
                if (grid[i][j] == 'S')
                    player = {i, j};
                else if (grid[i][j] == 'B')
                    box = {i, j};
                else if (grid[i][j] == 'T')
                    finish = {i, j};
                else if (grid[i][j] == '#')
                    visitable[i * m + j] = false;
            }
        }
        queue<Objective> q;
        q.emplace(player, box);
        reachablePoints.reserve(4);

        int ans = 1;
        while (!q.empty()) {
            for (int s = q.size(); s; --s) {
                Objective obj = q.front();
                q.pop();
                completeObjective(visitable, obj);
                for (auto p : reachablePoints) {
                    Point newBox{(int8_t)((obj.box.x << 1) - p.x), (int8_t)((obj.box.y << 1) - p.y)};
                    if (newBox == finish)
                        return ans;
                    visited.insert(uniqueId(p.x * m + p.y, obj.box.x * m + obj.box.y));
                    q.emplace(p, newBox);
                }
                reachablePoints.clear();
            }
            ++ans;
        }
        return -1;
    }
};
