class Solution {
private:
    int m, n;
    vector<pair<int, int>> dir;
    struct Hash {
        size_t operator()(const vector<int> &v) const {
            return v[0] * 20 + v[1] + v[2] * 20 + v[3];
        }
    };
    unordered_set<vector<int>, Hash> visited;
    struct Hash2 {
        size_t operator()(const vector<int> &v) const {
            return v[0] * 20 + v[1];
        }
    };

public:
    int minPushBox(vector<vector<char>>& grid) {
        m = grid.size();
        n = grid[0].size();
        dir = vector<pair<int, int>>{{-1, 0}, {1, 0}, {0, -1}, {0, 1}};
        // find target, box, person
        vector<int> target, box, person;
        for (int i = 0; i < m; i++) {
            for (int j = 0; j < n; j++) {
                if (grid[i][j] == 'S') {
                    person = vector<int>{i, j};
                } else if (grid[i][j] == 'B') {
                    box = vector<int>{i, j};
                } else if (grid[i][j] == 'T') {
                    target = vector<int>{i, j};
                }
            }
        }
        // BFS
        queue<vector<int>> q;
        q.push(vector<int>{box[0], box[1], person[0], person[1]});
        visited.insert(vector<int>{box[0], box[1], person[0], person[1]});
        int res = 0;
        while (!q.empty()) {
            int N = q.size();
            while (N--) {
                vector<int> cur = q.front();
                q.pop();
                if (cur[0] == target[0] && cur[1] == target[1])
                    return res;
                for (auto &d : dir) {
                    int b_row_nex = cur[0] + d.first;
                    int b_col_nex = cur[1] + d.second;
                    int p_row_nex = cur[0] - d.first;
                    int p_col_nex = cur[1] - d.second;
                    // judge position valid
                    if (valid(grid, b_row_nex, b_col_nex) && valid(grid, p_row_nex, p_col_nex)) {
                        // judge person reach and visited
                        if (canReach(grid, cur, p_row_nex, p_col_nex) &&
                            visited.find(vector<int>{b_row_nex, b_col_nex, cur[0], cur[1]}) == visited.end()) {
                            q.push(vector<int>{b_row_nex, b_col_nex, cur[0], cur[1]});
                            visited.insert(vector<int>{b_row_nex, b_col_nex, cur[0], cur[1]});
                        }
                    }
                }
            }
            res += 1;
        }
        return -1;
    }

    bool canReach(vector<vector<char>> &grid, vector<int> &start, int &p_row, int &p_col) {
        // judge whether person could reach (p_row, p_col)
        queue<vector<int>> q;
        unordered_set<vector<int>, Hash2> visited2;
        q.push(vector<int>{start[2], start[3]});
        visited2.insert(vector<int>{start[2], start[3]});
        while (!q.empty()) {
            vector<int> cur = q.front();
            q.pop();
            if (cur[0] == p_row && cur[1] == p_col)
                return true;
            for (auto &d : dir) {
                int row_nex = cur[0] + d.first;
                int col_nex = cur[1] + d.second;
                if (valid(grid, row_nex, col_nex) && !(row_nex == start[0] && col_nex == start[1]) &&
                    visited2.find(vector<int>{row_nex, col_nex}) == visited2.end()) {
                    q.push(vector<int>{row_nex, col_nex});
                    visited2.insert(vector<int>{row_nex, col_nex});
                }
            }
        }
        return false;
    }

    bool valid(vector<vector<char>> &grid, int row, int col) {
        return row >= 0 && row < m && col >= 0 && col < n && grid[row][col] != '#';
    }
};
