                    visited2.find(vector<int>{row_nex, col_nex}) == visited2.end()) {
                    q.push(vector<int>{row_nex, col_nex});
                    visited2.insert(vector<int>{row_nex, col_nex});
                }
            }
        }
        return false;
    }

    bool valid(vector<vector<char>> &grid, int row, int col) {
        return row >= 0 && row < m && col >= 0 && col < n && grid[row][col] != '#';
    }
