public class Solution
{
        int rows;
        int cols;
        char[][] mat;
        Dictionary<((int, int), (int, int), (int, int)), bool> route;

        public int MinPushBox(char[][] grid)
        {
            mat = grid;
            rows = grid.Length;
            cols = grid[0].Length;

			//Cache to store if a route exists from point A to point B. (source, dest, box location)
            route = new Dictionary<((int, int), (int, int), (int, int)), bool>();

            (int, int) bp = (0, 0);   // box position
            (int, int) tp = (0, 0);   // target position
            (int, int) sp = (0, 0);   // player position

           // Traverse Through the matrix and store the above positions
            for (int i = 0; i < rows; i++)
            {
                for (int j = 0; j < cols; j++)
                {
                    if (grid[i][j] == 'B')
                    {
                        bp.Item1 = i;
                        bp.Item2 = j;
                        grid[i][j] = '.';
                    }
                    else if (grid[i][j] == 'S')
                    {
                        sp.Item1 = i;
                        sp.Item2 = j;
                        grid[i][j] = '.';
                    }
                    else if (grid[i][j] == 'T')
                    {
                        tp.Item1 = i;
                        tp.Item2 = j;
                        grid[i][j] = '.';
                    }
                }
            }

            var q = new Queue<((int, int), (int, int), int)>();

            q.Enqueue((bp, sp, 0));
            var v = new HashSet<(int, int, int, int)>();

			//Start BFS from the Box position.
            while (q.Count > 0)
            {
                    var cur = q.Dequeue();
                    var cbp = cur.Item1;     // current box position
                    var cpp = cur.Item2;     // current player position
                    var push = cur.Item3;   // number of pushes upto this position

                    if (cbp == tp)               // if current box posotion == target then we are done
                        return push;

                    push++;
                    //Console.WriteLine();
                    //Console.WriteLine($"DeQueue {cbp.Item1}:{cbp.Item2}  {cpp.Item1}:{cpp.Item2} -> ");
                    for (int dir = 0; dir < 4; dir++)   // from one cell try to go all 4 ways
                    {
                        var nbp = cbp;  //nbp - next box position. set next position basing on direction

                        switch (dir)
                        {
                            case 0:
                                nbp.Item2 = cbp.Item2 + 1; //right
                                break;
                            case 1:
                                nbp.Item1 = cbp.Item1 + 1; //down
                                break;
                            case 2:
                                nbp.Item2 = cbp.Item2 - 1; //left
                                break;
                            case 3:
                                nbp.Item1 = cbp.Item1 - 1; //up
                                break;
                        }

                        if (v.Contains((cbp.Item1, cbp.Item2, nbp.Item1, nbp.Item2)))
                            continue;

                        if (CellFree(nbp.Item1, nbp.Item2) && CanPush(cbp, cpp, dir))
                        {
                            //Console.WriteLine($"  Can Push From {cbp.Item1}:{cbp.Item2} To {nbp.Item1}:{nbp.Item2}  player at {cpp.Item1}:{cpp.Item2} {dir}");
                            q.Enqueue((nbp, cbp, push));  //cbp becomes nbp & cpp becomes cbp after a push.
                            v.Add((cbp.Item1, cbp.Item2, nbp.Item1, nbp.Item2));
                        }
                    }
            }

            return -1;
        }

        // Check if the Box can be pushed from point A to Point B.
        public bool CanPush((int, int) cbp, (int, int) cpp, int direction)
        {
            var dpi = cbp.Item1;  // destination player position i
            var dpj = cbp.Item2;  // destination player position j

            int i = cpp.Item1;
            int j = cpp.Item2;

            if (!CellFree(dpi, dpj) || !CellFree(i, j))
                return false;

	       // set the player location. where the player should be in order to push the box
		   // basically one cell before the box.
            switch (direction)
            {
                case 0:
                    dpj = dpj - 1; // right
                    break;
                case 1:
                    dpi = dpi - 1; // down
                    break;
                case 2:
                    dpj = dpj + 1; // left
                    break;
                case 3:
                    dpi = dpi + 1; //up
                    break;
            }

           // [dpi, dpj] is where the player needs to be in order to push the box. 
		   // so see if the player has a clear path from cpp to [dpi, dpj].

            if (cpp.Item1 == dpi && cpp.Item2 == dpj)
                return true;

            if (!CellFree(dpi, dpj))
                return false;

            if(route.TryGetValue((cpp, (dpi, dpj), cbp), out bool exists))
                return exists;

			// If player  has path from A to B then he can take same path from B to A as well.
			// no need to traverse the graph to check if a path exists from B to A again. 
            if(route.TryGetValue(((dpi, dpj), cpp, cbp), out exists))
                return exists;

            exists = dfs(cpp, (dpi, dpj), cbp, new bool[rows, cols]);

            route.Add((cpp, (dpi, dpj), cbp), exists);
            return exists;
        }

        // If the player is at a different lcoation, then do a dfs and check if 
		// player can reach to the target location so that he can push the box.
        public bool dfs((int, int) cpp, (int, int) dpp, (int, int) cbp, bool[,] v)
        {
            if (cpp == dpp)
                return true;

            int i = cpp.Item1;
            int j = cpp.Item2;


            if (!CellFree(i, j) || v[i, j] || cpp == cbp)
                return false;

            v[i, j] = true;

            if (dfs((i + 1, j), dpp, cbp, v))
                return true;

            if (dfs((i, j + 1), dpp, cbp, v))
                return true;

            if (dfs((i - 1, j), dpp, cbp, v))
                return true;

            if (dfs((i, j - 1), dpp, cbp, v))
                return true;

            return false;
        }

        public bool CellFree(int i, int j)
        {
            if (i < 0 || i >= rows || j < 0 || j >= cols || mat[i][j] != '.')
                return false;

            return true;
        }
}
