const ptsEqual = (p1,p2) => p1[0] === p2[0] && p1[1] === p2[1]
//intially find our box and player
const findTokens = (grid)=>{
    const output = {
        box: null,
        player: null
    }
    for(let i = 0; i<grid.length ;i++){
        for(let j = 0; j<grid[0].length; j++){
            if(grid[i][j] === 'B') output.box = [i,j]
            if(grid[i][j] === 'S') output.player = [i,j]

        }
    }
    return output
}
//check if player can reach with BFS
const pCR = (pC,dC,grid,box)=>{

    if(ptsEqual(pC,dC)) return true
    const tried = new Set()
    const queue = [pC]

    while(queue.length){
        const [i,j] = queue.shift()

        const key = `${i}-${j}`
        if(tried.has(key)) continue;
        else tried.add(key)



        for(const dir of dirs){
            let newI = i + dir[0]
            let newJ = j + dir[1]

            // note edge case where we can't go through the box
        if(!vPt([newI,newJ],grid) || grid[newI][newJ] === '#' || ptsEqual([newI,newJ],box)) continue


            if(ptsEqual([newI,newJ],dC)) return true
            else {
                queue.push([newI,newJ])
            }

        }
    }
    return false
}

//use a BFS approach to explore if we can push box in the right direction
var minPushBox = function(grid) {

    let minMoves = Infinity

    const { box, player } = findTokens(grid)

    //we'll set the box and player to open spaces as we'll be exploring using bfs and our 'current' positions of
    // both tokens will be in our queue objects
    grid[box[0]][box[1]] = '.'
    grid[player[0]][player[1]] = '.'

    const queue = [{
        box,
        player,
        moves: 0
    }]
    const tried = new Set()
    while(queue.length){
        const { box, player, moves} = queue.shift()


        const key = `${box[0]}-${box[1]}--${player[0]}-${player[1]}`
            if(tried.has(key)) continue;
            else tried.add(key)


        for(const dir of dirs){
            const moveBoxTo = [box[0] + dir[0], box[1]+dir[1]]
            const playerSt = [box[0] - dir[0], box[1] - dir[1]]



            // check for valid pt
            if(!vPt(moveBoxTo,grid)|| !vPt(playerSt,grid)) {
                // console.log(`${moveBoxTo} or ${playerSt} is not valid`)
                continue;

            };

            const boxToVal = grid[moveBoxTo[0]][moveBoxTo[1]]
            const playerStVal = grid[playerSt[0]][playerSt[1]]

            // happy path
            if(boxToVal === 'T' && pCR(player,playerSt,grid,box)){
                minMoves = Math.min(minMoves,moves+1)
                continue;
            }

            // if we can move the box, we'll add to the queue for a move possibility
            if(boxToVal !== '#' && pCR(player,playerSt,grid,box)){
                queue.push({
                    box:moveBoxTo,
                    player: box,
                    moves: moves + 1
                })
            }

        }
    }



    return minMoves === Infinity ? -1 : minMoves

};
