class Solution {
    private static final char WALL = '#';
    private static final char EMPTY = '.';
    private static final char TARGET = 'T';
    private static final char KEEPER = 'S';
    private static final char BOX = 'B';

    private static final int[][] dirs = {{0,1}, {1,0}, {-1,0}, {0,-1}};

    private char[][] field;
    private int m;
    private int n;
    private int bound;

    private boolean canMove(int x, int y) {
        return x >= 0 && x < m && y >= 0 && y < n && field[x][y] != WALL;
    }

    private void setField(int point, char value) {
        field[point / n][point % n] = value;
    }

    private boolean canAccess(int oldKeeper, int newKeeper) {
        Queue<Integer> queue = new LinkedList<>();
        queue.offer(oldKeeper);

        boolean[] visited = new boolean[m * n];
        visited[oldKeeper] = true;

        while (!queue.isEmpty()) {
            int point = queue.poll();

            if (point == newKeeper)
                return true;

            for (int[] dir : dirs) {
                int x = point / n + dir[0];
                int y = point % n + dir[1];

                if (!canMove(x, y))
                    continue;

                int next = x * n + y;

                if (!visited[next]) {
                    visited[next] = true;
                    queue.offer(next);
                }
            }
        }

        return false;
    }

    public int minPushBox(char[][] grid) {
        this.field = grid;
        this.m = field.length;
        this.n = field[0].length;
        this.bound = m * n;

        int box = 0;
        int target = 0;
        int keeper = 0;

        /*
            To simplify search space we keep only 
            walls and empty spaces on the board
        */
        for (int i = 0; i < m; i++) {
            for (int j = 0; j < n; j++) {
                char point = field[i][j];

                switch(point) {
                    case TARGET:
                        target = i * n + j; // save target coordinate
                        setField(target, EMPTY); // erase the field
                        break;
                    case KEEPER:
                        keeper = i * n + j; // same for keeper
                        setField(keeper, EMPTY);
                        break;
                    case BOX:
                        box = i * n + j; // same for box
                        setField(box, EMPTY);
                        break;
                }
            }
        }

        /*
            The trick here is to consider both
            box and keeper postions while searching
        */
        int[] startPoint = new int[] {box, keeper};
        int startHash = Arrays.hashCode(startPoint);

        Queue<int[]> queue = new LinkedList<>();
        queue.offer(startPoint);

        /*
            Having 20 * 20 = 400 max board size
			Arrays.hashCode() calculates the hash using next formula
			31 * box + keeper which equals to 12800 in our case
            so 2 ^ 14 = 16384 would be enouth to track visited points
        */
        boolean[] visited = new boolean[(int)Math.pow(2, 14)];
        visited[startHash] = true;

        int result = 0;
        while (!queue.isEmpty()) {
            int size = queue.size();

            while (0 < size--) {
                int[] oldPoint = queue.poll();

                int oldBox = oldPoint[0];
                int oldKeeper = oldPoint[1];

                if (oldBox == target)
                    return result;

                for (int[] dir : dirs) {
                    // next box position
                    int bX = oldBox / n + dir[0];
                    int bY = oldBox % n + dir[1];

                    /*
                        If we want to move the box one step to the left
                        [.][.][.]
                        [.][B][.] <- direction
                        [.][.][.]

                        We have to check if the keepoer can reach the position
                        one step before currnt box position from left
                        [.][.][.]
                        [.][B][K] <- direction
                        [.][.][.]

                        That's why we have to substruct current direction
                        from current box position to get next keeper position

                        Next box and keeper positions shell look like
                        [.][.][.]
                        [B][.][K]
                        [.][.][.]

                        Next move will start from there
                    */
                    int kX = oldBox / n - dir[0];
                    int kY = oldBox % n - dir[1];

                    if (!canMove(bX, bY) || !canMove(kX, kY))
                        continue;

                    int newBox = bX * n + bY;
                    int newKeeper = kX * n + kY;

                    int[] newPoint = new int[] {newBox, oldBox};
                    int newHash = Arrays.hashCode(newPoint);

                    if (!visited[newHash]) {

                        /*
                            While checking if keeper can reach next position
                            consider the box as the wall
                        */
                        setField(oldBox, WALL);

                        /*
                            Check if the keeper can reach next position
                        */
                        if (canAccess(oldKeeper, newKeeper)) {
                            visited[newHash] = true;
                            queue.offer(newPoint);
                        }

                        /*
                            Make box's old position an empty again
                        */
                        setField(oldBox, EMPTY);
                    }
                }
            }

            result++;
        }

        return -1;
    }
}
