public class Solution 
{
    int[] X = new int[4]{0, 0, -1, +1};
    int[] Y = new int[4]{-1, +1, 0, 0};

    int[] XOpposite = new int[4]{0, 0, +1, -1};
    int[] YOpposite = new int[4]{+1, -1, 0, 0};

    public int MinPushBox(char[][] grid) 
    {
        int COUNT = Int32.MaxValue;
        (int, int) start = (0, 0);
        (int, int) target = (0, 0);
        (int, int) box = (0, 0);

        for(int i = 0; i < grid.Length; i++)
        {
            for(int j = 0; j < grid[0].Length; j++)
            {
                if(grid[i][j] == 'S')
                    start = (i, j);
                else if(grid[i][j] == 'B')
                    box = (i, j);
                else if(grid[i][j] == 'T')
                    target = (i, j);
            }
        }

        HashSet<(int, int, int, int, int, int)> hashSet = new HashSet<(int, int, int, int, int, int)>();
        //bool[,] visited = new bool[grid.Length, grid[0].Length];
        Queue<(int, int, int, int, int)> queue = new Queue<(int, int, int, int, int)>();
        queue.Enqueue((box.Item1, box.Item2, start.Item1, start.Item2, 0));
        while(queue.Count > 0)
        {
            var dq = queue.Dequeue();
            box = (dq.Item1, dq.Item2);
            start = (dq.Item3, dq.Item4);
            int count = dq.Item5;

            if(box.Item1 == target.Item1 && box.Item2 == target.Item2)
               COUNT = Math.Min(count, COUNT); 

            for(int i = 0; i < 4; i++)
            {
                int x = box.Item1 + X[i];
                int y = box.Item2 + Y[i];

                int xOpposite = box.Item1 + XOpposite[i];
                int yOpposite = box.Item2 + YOpposite[i];

                if(x >= 0 && y >= 0 && x < grid.Length && y < grid[0].Length
                  && xOpposite >= 0 && yOpposite >= 0 && xOpposite < grid.Length && yOpposite < grid[0].Length
                  && !hashSet.Contains((x, y, xOpposite, yOpposite, box.Item1, box.Item2)) //visited[x, y] == false
                  && grid[x][y] != '#' && grid[xOpposite][yOpposite] != '#' ) // check where to visit
                {
                    if(CheckCanVisit(grid, start.Item1, start.Item2, 
                                     new bool[grid.Length, grid[0].Length], xOpposite, yOpposite, box.Item1, box.Item2)) //check if human can be on opposite side
                    {
                        queue.Enqueue((x, y, xOpposite, yOpposite,  count + 1));

                        hashSet.Add((x, y, xOpposite, yOpposite, box.Item1, box.Item2));
                    }

                }

            }

        }

        if(COUNT < Int32.MaxValue)
            return COUNT;
        else
            return -1;
    }

    private bool CheckCanVisit(char[][] grid, int x, int y, bool[,] visited, int targetX, int targetY, int blockX, int blockY)
    {
        if(x < 0 || y < 0 || x >= grid.Length || y >= grid[0].Length || visited[x, y] || grid[x][y] == '#' || (x == blockX && y == blockY))
            return false;
        else if(x == targetX && y == targetY)
            return true;

        visited[x, y] = true;
        bool found = false;

        for(int i = 0; i < 4; i++)
        {
            int xx = x + X[i];
            int yy = y + Y[i];
            found = found || CheckCanVisit(grid, xx, yy, visited, targetX, targetY, blockX, blockY);
        }

        return found;
    }

}
