```
class Solution:
    def minPushBox(self, grid: List[List[str]]) -> int:
        #because we only need to care about the push, we can igore the path that storekeeper get to the box
        #try in 4 directions that the box can be pushed, check if the store keeper can get to the box in that direction
        #keep track of the postion of the storekeeper and box, we skip the position of the two that has been seen before
        #O(n^2) time and O(n^2) space 

        m = len(grid)
        n = len(grid[0])
        seen = set()
        for i in range(m):
            for j in range(n):
                if grid[i][j] == "S":
                    si, sj = i, j
                if grid[i][j] == "B":
                    bi, bj = i, j
                if grid[i][j] == "T":
                    ti, tj = i, j
        return self.getTarget(grid, si, sj, bi, bj, ti, tj)

    def getTarget(self, grid, si, sj, bi, bj, ti, tj):
        m = len(grid)
        n = len(grid[0])
        q = deque([(si, sj, bi, bj)])
        push = 0
        seen = set()
        while q:
            for _ in range(len(q)):
                si, sj, bi, bj  = q.pop()
                if bi == ti and bj == tj:
                    return push
                seen.add((si, sj, bi, bj))
                for x, y in [(1, 0), (-1, 0), (0, 1), (0, -1)]:
                    ni, nj = bi + x, bj + y
                    if 0 <= ni < m and 0 <= nj < n and grid[ni][nj] != "#" and self.canArrive(grid, bi - x, bj - y, bi, bj, si, sj):
                        if (bi, bj, ni, nj) not in seen:
                            q.appendleft((bi, bj, ni, nj))
                            seen.add((bi, bj, ni, nj))
            push += 1

        return -1

    def canArrive(self, grid, di, dj, bi, bj, si, sj):
        m = len(grid)
        n = len(grid[0])
        seen = set()
        q = deque([(si, sj)])
        while q:
            si, sj = q.pop()
            if si == di and sj == dj:
                return True
            for x, y in [(1, 0), (-1, 0), (0, 1), (0, -1)]:
                ni, nj = si + x, sj + y

                if 0 <= ni < m and 0 <= nj < n and grid[ni][nj] != "#" and not (ni == bi and nj == bj) and (ni, nj) not in seen:
                    q.appendleft((ni, nj))
                    seen.add((ni, nj))

        return False
