    //   - space: O(1), only constant space is used.

    func removeElement(_ nums: inout [Int], _ val: Int) -> Int {
        guard !nums.isEmpty else { return 0 }
        var i = 0

        for num in nums {
            guard num != val else { continue }
            nums[i] = num
            i += 1
        }

        return i
    }

}
