class Solution {
public:
    int removeElement(vector<int>& n, int val) {
        int i=0,j=n.size()-1,k=0;
        while(i<=j)
        {
            if(n[i]==val && n[j]!=val)
            {
                swap(n[i],n[j]);
                k++;
                i++;
                continue;
            }
            if(n[i]!=val)
             {
                k++;
                i++;
             }
            if(n[j]==val)
             j--;
        }
  return k;  }
};
```

