public int removeElement(int[] nums, int val) {
    int countVal = 0;

    for (int i = 0, count = 0; count < nums.length; i++, count++) {
        if (nums[i] == val) {
            move(i, nums, countVal);
            countVal++;
            i--;
        }
    }

    return nums.length - countVal;
}

private void move(int index, int[] nums, int countVal) {
    for (int i = index; i < nums.length - 1 - countVal; i++) {
        nums[i] = nums[i + 1];
    }
}

