```
class Solution {
public:
    int removeElement(vector<int>& nums, int val) {
        int left = 0;
        int right = nums.size() - 1;
        int count = 0;
        while (left <= right) {
            if (nums[left] == val) {
                while (nums[right] == val && left <= right) {
                    right--;
                    count++;
                }

                if (left <= right) {
                    //swap(nums[left], nums[right]);
                    int t = nums[left];
                    nums[left] = nums[right];
                    nums[right] = t; 
                    right--;
                    count++;
                }
            }

            left++;
        }

        return nums.size() - count;
    }
};
```
