            string word = q.front(); q.pop();
            auto itr = unvisited.begin();
            while (itr != unvisited.end()) {
                string adjWord = *itr;
                if (oneCharDiff(word, adjWord)) {
                    visited[adjWord] = visited[word] + 1;
                    if (adjWord == end) 
                        return visited[adjWord];
                    itr = unvisited.erase(itr);  // tricky here
                    q.push(adjWord);
                }
                else
                    ++itr;
            }
        }
        return 0;
