    inline bool oneCharDiff(const string& str1, const string& str2)
    {
        int diff = 0;
        for (int i = 0; i < str1.size(); ++i)  {
            if (str1[i] != str2[i]) 
                ++diff;
            if (diff > 1) 
                return false;  // perhaps quicker
        }
        return diff == 1;
    }

