        queue<pair<string,int>>q;
        for(int i=0;i<wordList.size();i++)
        {
            dict.insert(wordList[i]);
        }
        q.push({b,1});
        while(!q.empty())
        {
            auto node=q.front();
            q.pop();
            if(node.first==e)
            {
                return node.second;
            }
            for(int i=0;i<node.first.length();i++)
