                    char x=tmp[i];
                    tmp[i]=c;
                    if(dict.find(tmp)!=dict.end())
                    {
                        q.push({tmp,node.second+1});
                        dict.erase(tmp);
                    }
                }
            }
        }
        return 0;
    }
};
