    const queueBegin = [nodeBegin];
    const queueEnd = [nodeEnd];

    while (queueBegin.length || queueEnd.length) {
        nodeBegin = queueBegin.shift() || new GraphNode();
        nodeEnd = queueEnd.shift() || new GraphNode();

