            int size = i.second.size();
            if(size == 1) continue;

            for(int j=0;j<size-1;++j)
                for(int k=j+1;k<size;++k)
                {
                    G[i.second[j]].push_back(i.second[k]);
                    G[i.second[k]].push_back(i.second[j]);
                }
        }

        // BFS and keep track of levels
        Q.push(beginWord);
        visited[beginWord] = true;
        level[beginWord] = 1;

        while(!Q.empty())
        {
            string curr = Q.front();
            Q.pop();

            for(string i : G[curr])
            {
                if(!visited[i])
                {
                    Q.push(i);
                    level[i] = level[curr] + 1;
                    visited[i] = true;

                    if(i == endWord) return level[endWord];
                }
            }
        }

        return 0;
    }
