            for (auto& h : head) wordList.erase(h);
            for (auto src : head) {
                for (int i = 0; i<size; ++i) {
                    char tmp = src[i];
                    for (src[i] = 'a'; src[i] <= 'z'; ++src[i]) {
                        auto it = wordList.find(src);
                        if (it != wordList.end()) {
                            if (tail.find(*it) != tail.end()) return dist;
                            next.insert(*it);
                        }
                    }
                    src[i] = tmp;
                }
            }
            ++dist;
            head.swap(next);
            next.clear();
            if (head.size()>tail.size()) head.swap(tail);
        }
        return 0;
    }
};
