class Solution {
    public int ladderLength(String beginWord, String endWord, List<String> wordList) {
        if (beginWord == null && endWord == null && wordList.size() == 0)
            return 0;
        Map<String, List<String>> graph = new HashMap();
        List<String> words = new ArrayList();
        words.add(beginWord);
        words.addAll(wordList);

        for (String word : words) {
            graph.put(word, new ArrayList());
        }

        for (int i = 0; i<words.size()-1; i++) {
            for (int j = i+1; j<words.size(); j++) {
                String w1 = words.get(i);
                String w2 = words.get(j);
                if (isOneEditAway(w1, w2)) {
                    graph.get(w1).add(w2);
                    graph.get(w2).add(w1);
                }
            }
        }
        return levelOrderTraverse(graph, beginWord, endWord, new HashSet<String>());
    }

    private int levelOrderTraverse(Map<String, List<String>> graph, String s, String d, HashSet<String> visited) {
        LinkedList<String> q = new LinkedList();
        q.add(s);
        int level = 0;
        while(!q.isEmpty()) {
            level++;
            int size = q.size();
            for (int i = 0; i<size; i++) {
                String word = q.poll();
                if (visited.contains(word))
                    continue;
                visited.add(word);
                if (word.equals(d))
                    return level;
                for (String adj : graph.get(word))
                    if (!visited.contains(adj))
                        q.add(adj);
            }
        }
        return 0;
    }

    private boolean isOneEditAway(String w1, String w2) {
        boolean diffFound = false;
        for (int i = 0; i<Math.min(w1.length(), w2.length()); i++) {
            if (w1.charAt(i) != w2.charAt(i)) {
                if (diffFound) {
                    return false;
                } else {
                    diffFound = true;
                }
            }
        }
        return true;
    }
}
