                for(int j = 0; j < top.size(); j++){
                    char ch = top[j];

                    for(char c = 'a'; c <= 'z'; c++){
                        top[j] = c;

                        if(top == endWord) return count+1;
                        if(st.find(top) == st.end()) continue;

                        st.erase(top);
                        word.push(top);
                    }
                    top[j] = ch;
                }
            }
        }

        return 0;
    }
};
