            for i:=0; i<len(w); i++ {
                for ch:='a'; ch<='z'; ch++ {
                    ww := w[:i] + string(ch) + w[i+1:]
                    if findInMap(ww) {
                        queue = append(queue, ww)
                        delete(mp, ww)
                    }
                }
            }
            queue = queue[1:]
        }
        steps++
    }

    return 0
