                        }
                        if(visitedMap.containsKey(neighString) && !visitedMap.get(neighString)){
                            queue.offer(neighString);
                            visitedMap.put(neighString, true);
                        }
                    }
                }
            }
            level++;
        }
        return 0;
    }
}
