class Solution {
private:
    int foobar(deque<string>& Q, unordered_map<string, vector<string>>& dict, unordered_map<string, int>& distance, unordered_map<string, int>& otherDistance) {
        string u = Q.front();
        Q.pop_front();

        if (otherDistance[u])
            return distance[u] + otherDistance[u] - 1;

        for (int i = 0; i < u.size(); ++i) {
            string intermediate = u.substr(0, i) + '*' + u.substr(i+1);

            for (const string& v: dict[intermediate])
                if (!distance[v]) {
                    distance[v] = distance[u] + 1;
                    Q.push_back(v);
                }
        }

        return 0;
    }

public:
    int ladderLength(string beginWord, string endWord, vector<string>& wordList) {
        if (find(wordList.cbegin(), wordList.cend(), endWord) == wordList.cend())
            return 0;

        int N = wordList.size();
        int M = beginWord.size();
        unordered_map<string, vector<string>> dict;

        for (int i = 0; i < M; ++i) {
            string intermediate = beginWord.substr(0, i) + '*' + beginWord.substr(i+1);
            dict[intermediate].push_back(beginWord);
        }

        for (const string& word: wordList)
            for (int i = 0; i < M; ++i) {
                string intermediate = word.substr(0, i) + '*' + word.substr(i+1);
                dict[intermediate].push_back(word);
            }

        deque<string> beginQ, endQ;
        unordered_map<string, int> beginDistance, endDistance;

        beginQ.push_back(beginWord);
        endQ.push_back(endWord);
        beginDistance[beginWord] = endDistance[endWord] = 1;
        while (!beginQ.empty() && !endQ.empty()) {
            int res = foobar(beginQ, dict, beginDistance, endDistance);

            if (res) return res;

            res = foobar(endQ, dict, endDistance, beginDistance);

            if (res) return res;
        }

        return 0;
    }
};
