public int ladderLength(String beginWord, String endWord, List<String> wordList) {

        //bi directional bfs to cut down on runtime

        Set<String> dict = new HashSet<>(wordList), start = new HashSet<>(), end = new HashSet<>();

        if(!dict.contains(endWord) || endWord.equals(beginWord)) return 0;

        int length = 1;

        start.add(beginWord); end.add(endWord);

        //remove begin and end from dict so we don't attempt to include these in building towards the result

        dict.remove(endWord);

        if(dict.contains(beginWord))
            dict.remove(beginWord);

        //start will always be the smaller set which makes bfs faster
        while(!start.isEmpty()){

            length++;

            //iterate all intermediary words from start, we won't remove from the start set however we will not re-use it in later iterations therefore values are lost

            Set<String> neighbors = new HashSet<>(); //get all neighbors of all words in the start set

            for(String word : start){

                char[] chars = word.toCharArray();

                for(int idx = 0; idx < word.length(); idx++){

                    //save the char for replacing it later
                    char charAtIdx = chars[idx];

                    for(int c = 'a'; c <= 'z'; c++){

                        chars[idx] = (char) c;

                        String possibleNeighbor = String.valueOf(chars);

                        if(end.contains(possibleNeighbor)) return length;

                        if(dict.contains(possibleNeighbor)){

                            neighbors.add(possibleNeighbor);
                            dict.remove(possibleNeighbor);
                        }
                    }

                    //replace the char at the current idx
                    chars[idx] = charAtIdx;
                }
            }

            //make smart the smaller between neighbors and end
            start = neighbors.size() < end.size() ? neighbors : end;
            end = (start == end) ? neighbors : end;
        }

        return 0;
    }
