class Solution {
    typedef pair<string,int> psi;
public:
    int ladderLength(string beginWord, string endWord, vector<string>& wordList) {

        unordered_set<string> dict;
        bool found = false;
        for( string& itr : wordList)
        {
            if( itr == endWord)
                found = true;
            dict.insert(itr);                
        }

        if( found == false)
            return 0;

        queue<psi> q;
        q.push({beginWord,1});

        while(!q.empty())
        {
            psi temp = q.front();
            q.pop();

            if( temp.first == endWord)
                return temp.second;

            string str = temp.first;
            for(int i = 0; i< str.length() ;i++)
            {
                for(char c = 'a' ; c<= 'z' ; c++)
                {
                    char x = str[i];
                    str[i] = c;
                    if( dict.count(str))
                    {
                        q.push({str , temp.second+1});
                        dict.erase(str);
                    }
                    str[i] = x;
                }
            }
        }
        return 0;
    }
};
