class Solution {
public:
    int ladderLength(string begin_word, string end_word, vector<string>& word_list) {
        std::unordered_map<string, vector<string>> word_map;
        for (const string &word : word_list) {
            for (size_t i = 0; i < word.size(); i++) {
                string word_key = word;
                word_key.at(i) = '_';
                word_map[word_key].push_back(std::move(word));
            }
        }

        std::unordered_set<string> seen;
        std::queue<std::pair<string, int>> word_queue;
        word_queue.emplace(begin_word, 1);
        while (not word_queue.empty()) {
            auto queue_front = word_queue.front();
            const string &popped_word = queue_front.first;
            word_queue.pop();
            if (popped_word == end_word)
                return queue_front.second;

            for (int i = 0; i < popped_word.size(); i++) {
                string word_key = popped_word;
                word_key.at(i) = '_';
                auto it = word_map.find(word_key);
                if (it == word_map.cend())
                    continue;

                for (const string &word : it->second) {
                    if (seen.count(word)) {
                        continue;
                    }

                    word_queue.emplace(word, queue_front.second + 1);
                    seen.emplace(word);
                }
            }
        }

        return 0;
    }
};
