class Solution:
    def ladderLength(self, beginWord: str, endWord: str, wordList: List[str]) -> int:
        words_dict = self.construct_dict(wordList)
        return self.bfs(beginWord, endWord, words_dict)

    def construct_dict(self, wordList):
        words_dict = {}
        for word in wordList:
            for i in range(len(word)):
                s = word[:i] + '_' + word[i+1:]
                if s not in words_dict:
                    words_dict[s] = [word]
                else:
                    words_dict[s].append(word)

        return words_dict

    def bfs(self, beginWord, endWord, words_dict):
        queue, visited = collections.deque(), set()
        queue.append((beginWord, 1))

        while queue:
            word, steps = queue.popleft()

            if word == endWord:
                return steps

            for i in range(len(word)):
