typedef pair<string,int> psi;
class Solution {
public:
    int ladderLength(string start, string end, vector<string>& wordList) {
        unordered_set<string> D;// dict
        for(auto x: wordList)
            D.insert(x);

        queue<pair<string,int>> q;
        q.push({start, 1});
        D.erase(start); // visited = true

        while(!q.empty()){
            psi x = q.front();
            q.pop();

            string str = x.first;
            int len = x.second;

            if(str == end)
                return len;

            for(int i = 0; i < int(str.size()); i++){
                for(char c = 'a'; c <= 'z'; c++){
                    char prev = str[i];
                    str[i] = c;
                    if(D.find(str) != D.end()){
                        q.push({str, len + 1});
                        D.erase(str);
                    }
                    str[i] = prev;
                }
            }                        
        }

        return 0;// none found
    }
};
```

